INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (1, 14, 'https://cf.geekdo-images.com/x3zxjr-Vw5iU4yDPg70Jgw__original/img/FpyxH41Y6_ROoePAilPNEhXnzO8=/0x0/filters:format(jpeg)/pic3490053.jpg', true, 4, 2, 'Brass: Birmingham', 224517, 120, 'Brass: Birmingham is an economic strategy game set during the industrial revolution in Birmingham. Players compete as entrepreneurs, developing industries, building networks, and exploiting market demands. The game introduces new actions and industry types, such as breweries and pottery, and features a dynamic scoring system. Players must strategically manage resources, sell goods, and score the most victory points to win.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (2, 13, 'https://cf.geekdo-images.com/-Qer2BBPG7qGGDu6KcVDIw__original/img/PlzAH7swN1nsFxOXbfUvE3TkE5w=/0x0/filters:format(png)/pic2452831.png', true, 4, 2, 'Pandemic Legacy: Season 1', 161936, 60, 'Pandemic Legacy is a co-operative campaign game where players work together to prevent the outbreak of four diseases across the world. Over the course of 12-24 sessions, players must travel, research cures, and treat disease hotspots. The game introduces new rules and components that can permanently alter the game, including character skills and the possibility of losing a character entirely.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (3, 14, 'https://cf.geekdo-images.com/sZYp_3BTDGjh2unaZfZmuA__original/img/7d-lj5Gd1e8PFnD97LYFah2c45M=/0x0/filters:format(jpeg)/pic2437871.jpg', true, 4, 1, 'Gloomhaven', 174430, 120, 'Gloomhaven is a Euro-inspired tactical combat game set in a persistent world. Players take on the role of adventurers exploring menacing dungeons, making decisions that impact the branching story. With innovative card play, players must strategically choose and combine abilities to defeat automated monsters and avoid exhaustion.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (4, 14, 'https://cf.geekdo-images.com/SoU8p28Sk1s8MSvoM4N8pQ__original/img/g4S18szTdrXCdIwVKzMKrZrYAcM=/0x0/filters:format(jpeg)/pic6293412.jpg', true, 4, 1, 'Ark Nova', 342942, 150, 'In Ark Nova, you will plan and design a modern zoo, build enclosures, accommodate animals, and support conservation projects worldwide. Use your set of five action cards to manage gameplay, including gaining new zoo cards, building structures, accommodating animals, carrying out tasks, and playing sponsor cards. With 255 cards featuring animals, specialists, special enclosures, and conservation projects, the game allows you to increase the appeal and scientific reputation of your zoo while collecting conservation points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (5, 14, 'https://cf.geekdo-images.com/_Ppn5lssO5OaildSE-FgFA__original/img/kVpZ0Maa_LeQGWxOqsYKP3N4KUY=/0x0/filters:format(jpeg)/pic3727516.jpg', true, 6, 3, 'Twilight Imperium: Fourth Edition', 233078, 480, 'Twilight Imperium (Fourth Edition) is a game of galactic conquest where players take on the role of one of seventeen factions competing for dominance through military, political, and economic means. Each faction offers a unique play experience, and the game board is constructed strategically using galaxy tiles. Players select strategy cards to determine their actions, move fleets, claim planets, engage in trade and warfare, and pass laws. Victory is achieved by completing objectives, both public and secret, and the game ends when a player reaches ten victory points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (6, 12, 'https://cf.geekdo-images.com/wg9oOLcsKvDesSUdZQ4rxw__original/img/thIqWDnH9utKuoKVEUqveDixprI=/0x0/filters:format(jpeg)/pic3536616.jpg', true, 5, 1, 'Terraforming Mars', 167791, 120, 'In Terraforming Mars, players take on the role of corporations working together to terraform the planet by raising its temperature, oxygen level, and ocean coverage. The game involves acquiring unique project cards, which can be anything from introducing plant life to establishing greenhouse gas industries, to increase resources and earn victory points. Players compete for the best city, ocean, and greenery tile placements, as well as for milestones and awards. The game ends when the global parameters reach their goals, and the player with the highest Terraform Rating and VPs wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (7, 14, 'https://cf.geekdo-images.com/PhjygpWSo-0labGrPBMyyg__original/img/mZzaBAEEJpMlHWWmC0R6Su0OibQ=/0x0/filters:format(jpeg)/pic5666597.jpg', true, 4, 1, 'Dune: Imperium', 316554, 120, 'Dune: Imperium is a deck-building game inspired by the Dune legacy. As a leader of a Great House, you must raise your banner, marshal your forces, and navigate the conflict on Arrakis - Dune. Utilize your deck to strategically send Agents, acquire cards, and strengthen your military or political influence. Defeat rivals, navigate factions, and secure victory for your House.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (8, 14, 'https://cf.geekdo-images.com/_HhIdavYW-hid20Iq3hhmg__original/img/PBzsLRqNKQKJxGnzpb7o3qLWPQM=/0x0/filters:format(jpeg)/pic5055631.jpg', true, 4, 1, 'Gloomhaven: Jaws of the Lion', 291457, 120, 'Gloomhaven: Jaws of the Lion is a standalone game set before the events of Gloomhaven. It features four new playable characters and introduces a new campaign with 25 scenarios. This version is aimed at a more casual audience, with simplified rules and a tutorial to create a smoother entry into the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (9, 13, 'https://cf.geekdo-images.com/ImPgGag98W6gpV1KV812aA__original/img/38jB7fN07DwlrGKYAf-J0vsNdgs=/0x0/filters:format(jpeg)/pic1215633.jpg', true, 4, 2, 'War of the Ring: Second Edition', 115746, 180, 'War of the Ring is a strategic game where one player controls the Free Peoples and the other controls the Shadow Armies. The objective is to either achieve a military victory by conquering cities or to successfully complete the quest of the Ringbearer. Players take turns using action dice to perform various actions such as moving armies, recruiting troops, and playing event cards that can change the course of the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (10, 14, 'https://cf.geekdo-images.com/7SrPNGBKg9IIsP4UQpOi8g__original/img/GKueTbkCk2Ramf6ai8mDj-BP6cI=/0x0/filters:format(jpeg)/pic4325841.jpg', true, 4, 2, 'Star Wars: Rebellion', 187645, 240, 'Star Wars: Rebellion is an epic board game where two to four players battle it out as either the Galactic Empire or Rebel Alliance. Each side has different win conditions, with the Empire aiming to crush the Rebel Alliance and the Rebels trying to rally the galaxy to their cause. With over 150 miniatures and two game boards representing famous Star Wars systems, Rebellion offers an immersive and cinematic experience that incorporates notable characters and secret missions from the classic trilogy.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (11, 13, 'https://cf.geekdo-images.com/kjCm4ZvPjIZxS-mYgSPy1g__original/img/9uLd9C3XAvInLCLhAoXqKVk56zs=/0x0/filters:format(jpeg)/pic7013651.jpg', true, 4, 1, 'Spirit Island', 162886, 120, 'In the world where magic still exists, players take on the roles of spirits of the land in Spirit Island. This cooperative game focuses on defending the island and its native inhabitants from invading forces. Players use their unique elemental powers to strategically plan their actions and drive out the colonizing invaders.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (12, 12, 'https://cf.geekdo-images.com/hGWFm3hbMlCDsfCsauOQ4g__original/img/tjlflQtUPFiTpLpwk1NCVCS29Ic=/0x0/filters:format(png)/pic5375625.png', true, 4, 1, 'Gaia Project', 220308, 150, 'Gaia Project is a new game that follows in the footsteps of Terra Mystica. Players take on the roles of different factions, each with their own home planets, and compete to expand and develop by terraforming neighboring planets. With different skills and abilities, factions can improve in various areas of development and utilize advanced technology for special bonuses. The game offers a variable set-up and high replay value, making each playthrough unique.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (13, 14, 'https://cf.geekdo-images.com/fVwPntkJKgaEo0rIC0RwpA__original/img/1jawNpljTXwnT4km_2CjGwoUPR8=/0x0/filters:format(jpeg)/pic2663291.jpg', true, 4, 2, 'Through the Ages: A New Story of Civilization', 182028, 120, 'Through the Ages: A New Story of Civilization is a civilization building game where players aim to create the best civilization by managing resources, discovering technologies, electing leaders, building wonders, and maintaining a strong military. The game spans different ages and features card drafting as a primary mechanism. Victory is determined by the player with the highest cultural score at the end of the modern age.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (14, 13, 'https://cf.geekdo-images.com/pNCiUUphnoeWOYfsWq0kng__original/img/Iae47UtAd_RXVd5tJ3YzbDHOv4E=/0x0/filters:format(jpeg)/pic3530661.jpg', true, 2, 2, 'Twilight Struggle', 12333, 180, 'Twilight Struggle is a two-player game that simulates the forty-five year struggle between the Soviet Union and the United States. Players must navigate political, diplomatic, and military challenges as they vie for control and influence across the globe. The game features historical events and tensions, with the possibility of nuclear war, and offers a quick-playing and low-complexity experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (15, 12, 'https://cf.geekdo-images.com/u1l0gH7sb_vnvDvoO_QHqA__original/img/2zv_XMQoPFWk9Dn0oS4JY1IeFzw=/0x0/filters:format(jpeg)/pic4887376.jpg', true, 4, 2, 'Great Western Trail', 193738, 150, 'In the 19th century, players take on the role of a rancher herding cattle from Texas to Kansas City and sending them off by train to earn money and victory points. Along the Great Western Trail, players must keep their herd in good shape and strategically use buildings. Hiring staff such as cowboys, craftsmen, and engineers can also help in winning the game by maximizing victory points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (16, 14, 'https://cf.geekdo-images.com/7k_nOxpO9OGIjhLq2BUZdA__original/img/HlDb9F365w0tSP8uD1vf1pfniQE=/0x0/filters:format(jpeg)/pic3163924.jpg', true, 5, 1, 'Scythe', 169786, 115, 'Scythe is an alternate-history engine-building game set in the 1920s. Players represent characters from factions in Eastern Europe, vying to claim territory and resources around a mysterious factory. There is no player elimination and luck is mitigated through strategic choices, with a focus on upgrading actions, building structures, enlisting recruits, and activating mechs to enhance character abilities.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (17, 12, 'https://cf.geekdo-images.com/5CFwjd8zTcGYVUnkXh04hw__original/img/N8btACZrnEYK1amBNk26VBdcGwc=/0x0/filters:format(jpeg)/pic1176894.jpg', true, 4, 2, 'The Castles of Burgundy', 84876, 90, 'In The Castles of Burgundy, players take on the role of aristocrats aiming to build settlements, castles, and trade along the river in Medieval France. Each turn, players choose from four possible actions, including taking settlement tiles, delivering goods, or adjusting dice rolls using worker tokens. The game is played over five phases, with victory points being awarded for various achievements.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (18, 10, 'https://cf.geekdo-images.com/zdagMskTF7wJBPjX74XsRw__original/img/Ju836WNSaW7Mab9Vjq2TJ_FqhWQ=/0x0/filters:format(jpeg)/pic2576399.jpg', true, 2, 2, '7 Wonders Duel', 173346, 30, '7 Wonders Duel is a 2-player game where players acquire cards to advance their civilization and complete wonders over three ages. Unlike its parent game, players take turns selecting cards from a display, adding a timing element. Players can win by advancing their military, achieving scientific dominance, or earning the most points at the end of the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (19, 14, 'https://cf.geekdo-images.com/Oh3kHw6lweg6ru71Q16h2Q__original/img/yW7d4RNfU1ndISCaPlfGYUyxnRU=/0x0/filters:format(jpeg)/pic5235277.jpg', true, 6, 2, 'Eclipse: Second Dawn for the Galaxy', 246900, 200, 'Eclipse: Second Dawn for the Galaxy is a strategic game where players control interstellar civilizations competing for success. Explore star systems, research technologies, and build spaceships to wage war. Plan your strategy based on the strengths and weaknesses of your species while keeping an eye on rival civilizations.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (20, 14, 'https://cf.geekdo-images.com/tHVtPzu82mBpeQbbZkV6EA__original/img/3ffdJj5Pz6HQrg09Kh8ecTen-TY=/0x0/filters:format(jpeg)/pic3469216.jpg', true, 4, 2, 'Brass: Lancashire', 28720, 120, 'Brass: Lancashire is an economic strategy game set during the industrial revolution in Lancashire. Players compete as cotton entrepreneurs, developing industries and networks to meet demand for iron, coal, and cotton. The game is played in two halves, the canal phase and the rail phase, with scoring rounds in between. Players take turns performing actions such as building industries, developing, selling cotton, and taking loans. Strategic timing of card usage and resource management are key to success. The 2018 edition includes new artwork, components, and some rules changes for an enhanced gaming experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (21, 12, 'https://cf.geekdo-images.com/tAqLpWxQ0Oo3GaPP3MER1g__original/img/sA6r1p6BO_Tgyc3-xgIESC9Y-vg=/0x0/filters:format(jpeg)/pic5073276.jpg', true, 5, 1, 'Nemesis', 167355, 180, 'Nemesis is a sci-fi survival horror game where players must navigate a ship infested with hostile organisms. The goal is to complete one of the two objectives dealt at the start of the game and return to Earth safely. With unique crew members, individual skills, and evolving intruders, the game offers climactic moments and challenging obstacles that make each playthrough exciting.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (22, 13, 'https://cf.geekdo-images.com/CzwSm8i7tkLz6cBnrILZBg__original/img/BhJ3sB3uk-eSdR1iW4EP3cu0Wi0=/0x0/filters:format(jpeg)/pic3453267.jpg', true, 5, 2, 'Concordia', 124361, 100, 'Concordia is a peaceful strategy game set in Roman times. Players guide dynasties, send colonists to expand their empire, develop trade networks, and appease ancient gods for favor. With no reliance on luck, the game requires advanced planning and observation of opponents'' moves to emerge as the victor.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (23, 12, 'https://cf.geekdo-images.com/s9oGMCo1fcfV4Dk3EnqLZw__original/img/N1X-0JB1GapFVhl98nP4tNFXMcM=/0x0/filters:format(png)/pic3146943.png', true, 4, 1, 'A Feast for Odin', 177736, 120, 'A Feast for Odin is a board game where players explore new territories, raid, and engage in day-to-day activities to collect goods and achieve financial security. The game offers multiple pathways to victory and involves worker placement and strategic decision-making. The player with the highest-valued possessions at the end of the game wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (24, 13, 'https://cf.geekdo-images.com/hc2NDafu5c24iLJh_IZmyg__original/img/1Fpyz7j7rTvMPRiDdPjn0Vf0m2k=/0x0/filters:format(png)/pic4885780.png', true, 4, 2, 'Clank! Legacy: Acquisitions Incorporated', 266507, 120, 'Clank! Legacy: Acquisitions Incorporated is a deck-building game with legacy-style gameplay. Create your own franchise of the legendary adventuring company Acquisitions Incorporated and guide your treasure-hunters to successful corporate glory through multiple games. Experience a unique campaign as your game board, deck, and world evolve with each play.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (25, 10, 'https://cf.geekdo-images.com/yLZJCVLlIx4c7eJEWUNJ7w__original/img/cI782Zis9cT66j2MjSHKJGnFPNw=/0x0/filters:format(jpeg)/pic4458123.jpg', true, 5, 1, 'Wingspan', 266192, 70, 'Wingspan is a competitive card-driven board game where players take on the role of bird enthusiasts. The goal is to attract and discover the best birds for your network of wildlife preserves by creating powerful combinations in different habitats. Gain food tokens, lay eggs, and play unique bird cards to score points over 4 rounds. The game includes over 170 illustrated birds and a special promo pack in the base game box.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (26, 12, 'https://cf.geekdo-images.com/bre12I1YiXkZr7elvriz4A__original/img/_dZS7fVfdc4DhJPbqnDpwTT4uF0=/0x0/filters:format(jpeg)/pic5375624.jpg', true, 5, 2, 'Terra Mystica', 120677, 150, 'In the strategic game Terra Mystica, players govern one of 14 groups and must develop their home environment by terraforming neighboring landscapes. There are four religious cults to progress in and each group possesses special skills and abilities. Players take turns executing actions on their available resources, upgrading buildings, and developing their terraforming and seafaring skills. Proximity to other groups provides advantages but also poses challenges.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (27, 14, 'https://cf.geekdo-images.com/B5F5ulz0UivNgrI9Ky0euA__original/img/guEKCewM_2e5ugltSN3dTSwdZJI=/0x0/filters:format(jpeg)/pic3122349.jpg', true, 2, 1, 'Arkham Horror: The Card Game', 205637, 120, 'Arkham Horror: The Card Game is a Lovecraftian Living Card Game that blurs the lines between role-playing and card game experiences. In this game, you and your friends become characters in the town of Arkham, with unique talents and flaws. As you uncover terrifying mysteries, your strengths and weaknesses are reflected in your custom deck of cards. With each adventure, you delve deeper into the mystery, encountering cultists, haunted houses, and strange creatures, while your choices and actions have consequences that impact the campaign.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (28, 12, 'https://cf.geekdo-images.com/6GqH14TJJhza86BX5HCLEQ__original/img/CXqwimJPonWy1oyXEMgPN_ZVmUI=/0x0/filters:format(jpeg)/pic5674958.jpg', true, 4, 1, 'Lost Ruins of Arnak', 312484, 120, 'Lost Ruins of Arnak is a deck-building and worker placement game where players explore an uninhabited island, search for lost artifacts, and confront fierce guardians to uncover the island''s secrets. Players strategically use their cards for both deck-building effects and worker placement actions as they manage resources and make tactical decisions. With unique setups and a variety of actions, artifacts, and equipment cards, each game offers new challenges and encourages players to discover new strategies.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (29, 10, 'https://cf.geekdo-images.com/JUAUWaVUzeBgzirhZNmHHw__original/img/E0s2LvtFA1L5YKk-_44D4u2VD2s=/0x0/filters:format(jpeg)/pic4254509.jpg', true, 4, 2, 'Root', 237182, 90, 'Root is a game where 2 to 4 players battle for control of a vast wilderness. The game features different factions, including the Marquise de Cat, the Alliance, the Eyrie, and the Vagabond, each with unique abilities and victory conditions. Players must strategize and interact with each other to determine the fate of the woodland.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (30, 12, 'https://cf.geekdo-images.com/nagl1li6kYt9elV9jbfVQw__original/img/Qn6vlBaTUaHNFsqohIUjd0EA4z0=/0x0/filters:format(jpeg)/pic6228507.jpg', true, 4, 2, 'Orléans', 164928, 90, 'In Orléans, a medieval strategy game, you will recruit followers and use their abilities to gain supremacy through trade, construction, and science in medieval France. Farmers, knights, scholars, and more will help you expand your scope of action, secure mercantile expeditions, and make progress in science. With multiple paths to victory, the challenge is to strategically combine all elements to achieve your goals.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (31, 12, 'https://cf.geekdo-images.com/gDn7AhrDlmfCLSz9ZqoNFQ__original/img/yecB1xO32nnjBAyskVOTq9LBuLo=/0x0/filters:format(jpeg)/pic5988511.jpg', true, 4, 1, 'Great Western Trail: Second Edition', 341169, 150, 'Great Western Trail is a strategic game set in 19th century America where players act as ranchers herding cattle from Texas to Kansas City. Along the trail, players must maintain their herd and utilize various buildings wisely. Hiring capable staff and making smart decisions will determine who earns the most victory points and emerges as the winner. The second edition introduces new features such as solo play, new artwork, additional buildings, and more.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (32, 13, 'https://cf.geekdo-images.com/fjE7V5LNq31yVEW_yuqI-Q__original/img/HQ1ti16wT9lqja5_h3gUfHUIcVI=/0x0/filters:format(png)/pic3918905.png', true, 4, 1, 'Everdell', 199792, 80, 'Everdell is a dynamic tableau building and worker placement game set in a charming valley. Players lead a group of critters in constructing buildings, meeting characters, and hosting events. The game spans from winter to winter, with players striving to have the city with the most points at the end.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (33, 14, 'https://cf.geekdo-images.com/DUO2hz9AlLOH8p9ED-lCWg__original/img/PDDH38Vf9NEB_4ODURxcJKNBfVQ=/0x0/filters:format(jpeg)/pic1083380.jpg', true, 4, 1, 'Mage Knight Board Game', 96848, 240, 'The Mage Knight board game allows players to take control of powerful Mage Knights in a quest to explore and conquer the Atlantean Empire. Players can build armies, collect spells and actions, and conquer cities in competitive or cooperative scenarios. With elements of RPGs, deck-building, and traditional board games, Mage Knight delivers an immersive gaming experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (34, 13, 'https://cf.geekdo-images.com/l_PRU2lVlX9seScRFcvFlA__original/img/gDL7OZFlzoOFgU0VYlREs8P5hCQ=/0x0/filters:format(jpeg)/pic6500949.jpg', true, 6, 1, 'Viticulture Essential Edition', 183394, 90, 'In Viticulture, players take on the role of vineyard owners in rustic Tuscany. Their goal is to turn their small vineyards into successful wineries. Players must strategically allocate their workers throughout the year to complete tasks and make use of helpful visitors to expand their vineyards, build structures, plant vines, and fill wine orders. Viticulture Essential Edition includes the base game and popular modules from the Tuscany expansion.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (35, 14, 'https://cf.geekdo-images.com/jEPmWvvYpqkWrKOzqIHFsg__original/img/rkHKwkUqpQC7PAGG7n2gbrcQiUY=/0x0/filters:format(png)/pic4336469.png', true, 4, 1, 'Barrage', 251247, 120, 'In the dystopic 1930s, players compete as international companies to build dams and utilize hydroelectric energy in a contested Alpine region. The game includes innovative mechanisms where players must carefully plan their actions and manage their resources using a Construction Wheel. Players also strategically intercept and store water to produce energy, while meeting power requirements and completing personal contracts over five rounds.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (36, 10, 'https://cf.geekdo-images.com/VuBqZ1sMaDAVVHF_OEJP4g__original/img/jGWxczXC_VWZofeAmlO_DkB0hCI=/0x0/filters:format(jpeg)/pic5988903.jpg', true, 5, 2, 'The Crew: Mission Deep Sea', 324856, 20, 'In The Crew: Mission Deep Sea, players work together to search for the lost continent of Mu, diving deep into the abyss to uncover its secrets. Using a cooperative trick-taking mechanism, players must strategically communicate and win tricks in order to navigate the challenges and reach their goal. This new version of the game offers exciting surprises and requires flawless execution and a touch of luck to succeed.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (37, 12, 'https://cf.geekdo-images.com/wKwRk0wYBcrtLAfgn4PCdg__original/img/Wpp0vzsVe4HxXGUqiZ1hDvwAHZU=/0x0/filters:format(png)/pic6624445.png', true, 4, 1, 'Too Many Bones', 192135, 120, 'Too Many Bones is a dice-builder RPG that challenges traditional dice-rolling mechanics. Set in a fantasy world, players take on the role of a new race on a mission to defeat enemy forces and the notorious "baddie" responsible. With 100+ unique skill dice and 4-7 classes to choose from, players can team up or go solo in a 1-4 player Coop or Solo play campaign. Strategy is key as players make decisions about resting, exploring, and choosing which battles to pursue, all while facing plot twists and comedic relief along the way.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (38, 14, 'https://cf.geekdo-images.com/Wtxml94LAXsIWQCxGPS63Q__original/img/9c-MJB6x54vN5eB-1hT4XQaHtP8=/0x0/filters:format(png)/pic2649434.png', true, 5, 2, 'Food Chain Magnate', 175914, 240, 'Food Chain Magnate is a heavy strategy game where players build a fast food chain. Compete on a city map by purchasing, marketing, and selling, while also hiring key staff members. Designed for 2-5 serious gamers, the game takes approximately 2-4 hours to play.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (39, 13, 'https://cf.geekdo-images.com/oSM_AuKYfGIwOtKbVEsoVg__original/img/7DlaMCmuoJzm9AzelmStYIDgutI=/0x0/filters:format(png)/pic4503733.png', true, 5, 1, 'Pax Pamir: Second Edition', 256960, 120, 'Pax Pamir is a game where players take on the role of Afghan leaders in the 19th century, trying to build a new state after the collapse of the Durrani Empire. Players purchase cards from a central market and play them in front of them to build their own rows. Interference with other players is possible through various actions. Players must form coalitions to survive, and victory is determined through dominance checks. The game ends when a player achieves a lead of four or more victory points or after the fourth dominance check is resolved.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (40, 14, 'https://cf.geekdo-images.com/kRvUgYiaOq07kC67ZK5UoQ__original/img/cqng0e4S7Cj6j6Sb49-OCggGi-8=/0x0/filters:format(jpeg)/pic4900321.jpg', true, 4, 1, 'Marvel Champions: The Card Game', 285774, 90, 'Join Iron Man, Black Panther, Captain Marvel, Spider-Man, and other legendary heroes in Marvel Champions: The Card Game. This cooperative Living Card Game allows one to four players to embody iconic heroes and battle infamous villains from the Marvel Universe. With regular releases of new product, including heroes and scenarios, Marvel Champions offers endless opportunities for thrilling gameplay.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (41, 12, 'https://cf.geekdo-images.com/QFiIRd2kimaMqTyWsX0aUg__original/img/DOgIp57F7tKZvxeITGAd3e_Q9as=/0x0/filters:format(jpeg)/pic158548.jpg', true, 5, 3, 'Puerto Rico', 3076, 150, 'In Puerto Rico, players take on the roles of colonial governors on the island, striving to earn victory points by shipping goods or constructing buildings. Each player has their own board for managing city buildings, plantations, and resources, while also sharing resources, ships, and a trading house. The game features a role selection mechanic, where players choose a role card to take actions, with the player who selected the role receiving a bonus. As the game progresses, players must carefully manage their shipping tokens and consider their options to strategically end the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (42, 12, 'https://cf.geekdo-images.com/PwOwTVHovJAUQgghnGqCOg__original/img/1WL5-4DZi0hX76HEgsLIYRRaIh4=/0x0/filters:format(png)/pic4837710.png', true, 4, 1, 'Underwater Cities', 247763, 150, 'In Underwater Cities, players take on the role of powerful brains tasked with creating livable underwater areas. The game involves card placement, with players strategically placing colored cards into slots on the main board. They can then take actions and advantages to gather resources, build infrastructure, and collect cards to gain victory points. With over 220 cards and double-sided underwater areas, players have multiple avenues to achieve victory.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (43, 12, 'https://cf.geekdo-images.com/rz22tqa5PCYvK9oDjIbvxg__original/img/h9il8XnFB0iqHNHsNTPNGxJZWdM=/0x0/filters:format(jpeg)/pic1790789.jpg', true, 7, 1, 'Caverna: The Cave Farmers', 102794, 210, 'Caverna: The Cave Farmers is a worker-placement game that focuses on farming and mining. Players take on the role of a dwarf family living in a cave, cultivating the land and expanding their farm. Players can mine for ore, go on expeditions, and make strategic decisions to develop their home board and increase their wealth.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (44, 14, 'https://cf.geekdo-images.com/HkZSJfQnZ3EpS214xtuplg__original/img/Myy6IPDJDzLoPdXrPXVZcddBQoQ=/0x0/filters:format(jpeg)/pic2439223.jpg', true, 4, 2, 'Blood Rage', 170216, 90, 'Blood Rage is a Viking-themed game where players control their own clan''s warriors, leader, and ship. As Ragnarök approaches, players must strive for glory by invading, pillaging, battling opponents, completing quests, and increasing their clan''s stats. Strategy is key as players draft cards that grant boons and align with different Norse gods, influencing their gameplay and battle outcomes.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (45, 10, 'https://cf.geekdo-images.com/MjeJZfulbsM1DSV3DrGJYA__original/img/B374C04Eip7fmQBGJzgiOTp-jyQ=/0x0/filters:format(jpeg)/pic5100691.jpg', true, 4, 1, 'Cascadia', 295947, 45, 'Cascadia is a tile-laying and token-drafting game set in the Pacific Northwest. Players take turns building terrain areas and populating them with wildlife. The goal is to create matching terrain and strategically place wildlife tokens to score the most points based on randomized scoring cards.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (46, 15, 'https://cf.geekdo-images.com/31quLNzteInnevVRAABoow__original/img/3KfMPSj7jjhG0g5lQBsO-bn67D0=/0x0/filters:format(jpeg)/pic3499707.jpg', true, 4, 1, 'Anachrony', 185343, 120, 'In Anachrony, set in the late 26th century, players must rebuild Earth after a catastrophic explosion. Living in isolated factions, each with their own ideology, players must power up Time Rifts to reach back to the past and obtain resources, while also avoiding meddling too much with the time-space continuum. As an asteroid approaches Earth, players must race to collect victory points and become the new seat of power in the last major city on the planet.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (47, 12, 'https://cf.geekdo-images.com/dDDo2Hexl80ucK1IlqTk-g__original/img/toobKoejPiHpfpHk4SYd1UAJafw=/0x0/filters:format(jpeg)/pic831744.jpg', true, 5, 1, 'Agricola', 31260, 150, 'In Agricola, you''re a farmer in a wooden shack with your spouse. Each turn, you can take two actions from various options like collecting resources, building fences, and expanding your house. The game has different levels of complexity based on the use of cards, and players can experiment with different decks. Each round, players can take actions based on their strategy and try to outsmart their opponents.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (48, 14, 'https://cf.geekdo-images.com/Nm0Iw8NoiM9V8IsifimGBw__original/img/GNjjsgxq7wYO9pSIteDJe6Sxt00=/0x0/filters:format(jpeg)/pic4357658.jpg', true, 4, 1, 'On Mars', 184267, 150, 'In On Mars, you are the chief astronaut leading a private exploration company on Mars. Your goal is to develop a self-sustaining colony by accomplishing both the United Nations'' mission goals and your company''s agenda. Build structures, extract resources, and complete missions to gain Opportunity Points and become the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (49, 8, 'https://cf.geekdo-images.com/7ejbxsn-UtLl3ehamuAEdw__original/img/HJOZG6y87_skppfIqX-voRlhNxE=/0x0/filters:format(jpeg)/pic79413.jpg', true, 4, 2, 'Crokinole', 521, 30, 'Crokinole is a traditional dexterity game played on a circular wooden board. Players take turns shooting wooden disks across the board with their fingers, aiming to land in scoring regions. The game is usually played with two players or as a four-player game with partners, and the objective is to be the first to reach a set score, often 100.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (73, 8, 'https://cf.geekdo-images.com/aPSHJO0d0XOpQR5X-wJonw__original/img/AkbtYVc6xXJF3c9EUrakklcclKw=/0x0/filters:format(png)/pic6973671.png', true, 4, 2, 'Azul', 230802, 45, 'In the game Azul, players take on the role of tile-laying artists as they decorate the Royal Palace of Evora. They draft colored tiles from suppliers to their player board and score points based on how they arrange the tiles. Strategic placement and completing sets will earn extra points, while wasting supplies will harm players'' scores. The player with the highest score wins the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (50, 14, 'https://cf.geekdo-images.com/y0x1zbkpUXjddzWWnhekYw__original/img/2VlfzwT4b8f1s2HqbilcjR-bM3I=/0x0/filters:format(jpeg)/pic5581457.jpg', true, 4, 2, 'Pandemic Legacy: Season 0', 314040, 60, 'In Pandemic Legacy: Season 0, set during the Cold War, you and your team of medical graduates are recruited by the CIA to investigate and stop the development of a deadly Soviet bioweapon known as "Project MEDUSA". Use aliases to travel between Allied, Neutral, and Soviet cities, completing objectives and neutralizing enemy agents. Along with combating the pathogen, you must also contain enemy agents and coordinate with other covert operatives to ensure success. This standalone prequel offers new cards, rules, and conditions, allowing you to save the world from this bio-threat.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (51, 14, 'https://cf.geekdo-images.com/iEBr5o8AbJi9V9cgQcYROQ__original/img/ZNt6bSM6kCGFzN1_mzuPl4wCbyM=/0x0/filters:format(jpeg)/pic6177719.jpg', true, 4, 1, 'Frosthaven', 295770, 120, 'Frosthaven is a standalone adventure that takes place in a small outpost battling harsh weather and invasions. Players control a group of mercenaries who must face dangerous threats and make peace with new races to fight against sinister forces. The game offers new characters, races, enemies, items, and a 100-scenario campaign, with additional features such as mysteries to solve, a seasonal event system, and player control over village expansion. A crafting system is implemented for building items, and resources are valuable in the remote location.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (52, 14, 'https://cf.geekdo-images.com/Qtkb-UTvHa0-kxt_MK1nKw__original/img/wJiRr7lBmWSKcRS3lPpvKIPMgQQ=/0x0/filters:format(jpeg)/pic3763549.jpg', true, 4, 2, 'Pandemic Legacy: Season 2', 221107, 60, 'In Pandemic Legacy: Season 2, the world has been ravaged by a deadly plague. You and your team must navigate the remnants of civilization, known as "havens," to provide supplies and keep humanity alive. This cooperative game for 2 to 4 players features unique gameplay mechanics and a storyline that evolves with each playthrough.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (53, 13, 'https://cf.geekdo-images.com/Zdt8l4oTBpFICsMyNof7Jg__original/img/-okZ810RkA8fKKhIeZyX4cnqmbE=/0x0/filters:format(png)/pic5975244.png', true, 4, 1, 'Sleeping Gods', 255984, 1200, 'In Sleeping Gods, you and up to 3 friends become Captain Sofi Odessa and her crew, lost in a strange world in 1929 on your steamship, the Manticore. Work together to survive, explore islands, meet characters, and find the totems of the gods to return home. This campaign game can be played solo or with friends, and each session can last as long as you want, allowing you to easily pick up where you left off. Discover new lands, stories, and challenges in this atlas and storybook game, where your choices shape the characters and the plot.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (54, 14, 'https://cf.geekdo-images.com/L2Wn-zUqkcHgqvwvY212Ig__original/img/Htra4hvxjBlejtNEIUns_B3CNNc=/0x0/filters:format(jpeg)/pic4924232.jpg', true, 4, 1, 'Kanban EV', 284378, 180, 'In Kanban EV, players oversee the production of electric vehicles in a scheduling system called "kanban". As rookie employees, players manage suppliers, improve automobile parts, and work on the assembly line to boost production. Shrewd use of recycling facilities and limited supplies is necessary to meet factory goals and impress the factory manager. The game focuses on resource and time management, with the player earning production points (PP) to win.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (55, 14, 'https://cf.geekdo-images.com/LIooA9bTdjnE9qmhjL-UFw__original/img/Go6c8-ZiXomS8E7X4MBCdDd-aZc=/0x0/filters:format(jpeg)/pic3118622.jpg', true, 5, 1, 'Mansions of Madness: Second Edition', 205059, 180, 'Mansions of Madness: Second Edition is a fully co-operative horror and mystery board game for 1-5 players. Set in the same universe as Eldritch Horror and Elder Sign, players explore the streets of Innsmouth and the mansions of Arkham, solving puzzles, collecting weapons and information, and fighting monsters. With an immersive app to guide gameplay, players will need more than survival skills to conquer the evil horrors.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (56, 12, 'https://cf.geekdo-images.com/rMNa0k05zMdKgSEp26Q3Tw__original/img/yhnCcq9UplffqjSCe08mjTFvgOM=/0x0/filters:format(jpeg)/pic4917407.jpg', true, 4, 1, 'Maracaibo', 276025, 120, 'Maracaibo is a strategy game set in the Caribbean during the 17th century. Players compete to increase their influence in three nations over four rounds with a play time of 40 minutes per player. Sail on a round course, visit city tiles to perform actions or deliver goods, and engage in a quest mode with various tiles that tell a story. Manage your ship using cards and note that the Spanish and Portuguese editions include a mini-expansion.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (57, 12, 'https://cf.geekdo-images.com/OrHS8_a1CqSGiXeTfCk0Wg__original/img/hVArRkZYHiPTGTfi0DPR58i4o44=/0x0/filters:format(jpeg)/pic3209553.jpg', true, 4, 1, 'Lisboa', 161533, 120, 'Lisboa is a game set in the aftermath of the 1755 Lisbon earthquake, where players work together as members of the nobility to rebuild and develop the city. The game is played on a real map of downtown Lisbon, with players using their influence to collaborate with architects, develop commerce, and open buildings. The goal is to gather the most wigs, representing wealth and prestige, by the end of the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (58, 13, 'https://cf.geekdo-images.com/kXf7mDyDYuHg6oe8yTUIEA__original/img/f6-Au0KTnNR4nugSu2U-wPNbZCU=/0x0/filters:format(jpeg)/pic4604439.jpg', true, 4, 2, 'Tzolk''in: The Mayan Calendar', 126163, 90, 'Tzolkin: The Mayan Calendar introduces dynamic worker placement to the game. Players place their workers on rotating gears to take actions. They can either place workers or pick them up, using corn as currency. The game ends after one full rotation of the central gear, and there are multiple paths to victory such as pleasing the gods or building temples.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (59, 12, 'https://cf.geekdo-images.com/yd6LuatytHRhcFCxCf-EEg__original/img/OS13C6W4i1XW__wWVVVaqF7BV0c=/0x0/filters:format(jpeg)/pic4459753.jpg', true, 6, 2, 'Power Grid', 2651, 120, 'Power Grid is a strategic game where players compete to power the most cities. Players mark routes between cities and bid on power plants to generate energy. With each purchase, newer and more efficient plants become available, while players also need to acquire raw materials to power their plants. Upgrade your equipment and expand your network to win in this intense auction-style game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (60, 10, 'https://cf.geekdo-images.com/-vOrd4bOspibyohYExLqWg__original/img/iXU8a9WaVlwrkiEiOxWN06y9y5g=/0x0/filters:format(png)/pic6940449.png', true, 6, 1, 'Heat: Pedal to the Metal', 366013, 60, 'Heat: Pedal to the Metal is a fast-paced car racing game where players strategically manage their hand to control their car''s speed and avoid overheating. By selecting the right upgrades, players can navigate sharp curves and maintain top speeds. The game offers both single race and championship options, with changing weather, road conditions, and events to keep the gameplay exciting. Solo play and multiplayer options with automated drivers are also available.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (110, 12, 'https://cf.geekdo-images.com/36WIe0ZHkp5OvHOlB-8vog__original/img/4oIeldEZrg5Fi2YAlc5SzldywaA=/0x0/filters:format(jpeg)/pic4356580.jpg', true, 6, 2, 'Concordia Venus', 256916, 120, 'Concordia Venus is a standalone strategy game set in Roman times, where players must strategically develop their economy and outpace their rivals. Players use their playing cards to choose actions and gain victory points. With different maps and the addition of the goddess Venus, players can employ new strategies in teams or individually.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (61, 10, 'https://cf.geekdo-images.com/pH5LgRL4mNRon-2NsSDb1Q__original/img/8PEJOyuKGbTaEGbUbCoLQy_i65Y=/0x0/filters:format(png)/pic6137509.png', true, 4, 2, 'The Quacks of Quedlinburg', 244521, 45, 'In The Quacks of Quedlinburg, players are quack doctors concocting secret brews by adding ingredients to their pots. The higher the face value of the ingredient, the more valuable the potion becomes. But be cautious, as adding too many cherry bombs can cause your pot to explode. Gain victory points and coins at the end of each round to buy new ingredients, and the player with the most victory points after nine rounds wins the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (62, 12, 'https://cf.geekdo-images.com/SPuwc6RJ6y4PnTH36Pegsg__original/img/GcuvCavv-ZZP5_O1kCTrZELuZdY=/0x0/filters:format(png)/pic3511783.png', true, 4, 1, 'Clans of Caledonia', 216132, 120, 'Clans of Caledonia is a mid-to-heavy economic game set in 19th-century Scotland. Players represent historic clans and compete to produce, trade, and export agricultural goods and whisky. The game consists of five rounds, with each round including players taking turns, collecting resources, and receiving victory points based on the scoring tile.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (63, 10, 'https://cf.geekdo-images.com/98LnQShydr11OBKS46xY-Q__original/img/Q-ZkgnuBu9OTBr7J3qnqPjqamAE=/0x0/filters:format(jpeg)/pic5687013.jpg', true, 5, 2, 'The Crew: The Quest for Planet Nine', 284083, 20, 'The Crew: The Quest for Planet Nine is a co-operative trick-taking game where players embark on a space adventure to uncover the secrets of an unknown planet. With 50 exciting missions, players must work together to complete individual tasks and overcome challenges through effective communication. Each mission becomes increasingly difficult, but the game can be paused and continued later.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (64, 12, 'https://cf.geekdo-images.com/y7Rqd3S6J7vyVhicR1bqTQ__original/img/tQFXv1w2R-J-1cjyaKG9LAS2UOs=/0x0/filters:format(jpeg)/pic6091101.jpg', true, 5, 1, 'Le Havre', 35677, 150, 'In Le Havre, players take turns distributing newly supplied goods and taking actions. Actions can involve taking goods from offer spaces or using buildings to upgrade, sell, or build. Ships provide food for workers. After seven turns, a round ends with a Harvest and workers must be fed. The game ends after a set number of rounds and the player with the highest value in buildings, ships, and cash reserves wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (65, 12, 'https://cf.geekdo-images.com/4nhokcLdYoo6ulbZ1rmGgA__original/img/9ktZDu003VVVTw0RxD-ufLYV1HQ=/0x0/filters:format(png)/pic4462987.png', true, 4, 1, 'Paladins of the West Kingdom', 266810, 120, 'In Paladins of the West Kingdom, players take on the role of noble men and women defending their city against Saracens, Vikings, and Byzantines. By gathering workers and building fortifications, players can earn victory points. Throughout the game, players increase their faith, strength, and influence, which affects their score and the significance of their actions. The game concludes after the seventh round.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (66, 14, 'https://cf.geekdo-images.com/pIQ_MXvaoARRp1loCHJuHg__original/img/12Xvbw01hpsAjsO0xrajZt2b5HY=/0x0/filters:format(jpeg)/pic2247647.jpg', true, 5, 1, 'Star Wars: Imperial Assault', 164153, 120, 'Star Wars: Imperial Assault is a strategy board game that offers two distinct games of battle and adventure in the Star Wars universe, for two to five players. In the campaign game, players can either command the armies of the Galactic Empire or become heroes of the Rebel Alliance, engaging in covert operations to undermine the Empire. In the skirmish game, players compete in head-to-head tactical combat. The game also includes additional figure packs that allow heroes to fight alongside iconic characters from the Star Wars saga.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (67, 13, 'https://cf.geekdo-images.com/ie1GSt1XV04sXQXt-3O1UQ__original/img/59dZRZePEgtcupkRSA2zohZuHpM=/0x0/filters:format(png)/pic2503200.png', true, 4, 1, 'The Gallerist', 125153, 150, 'In The Gallerist, you play as an art dealer, museum curator, and artist manager. Your goal is to promote and sell art, build your reputation, and attract visitors to your gallery. Hire assistants, maximize your money, and achieve curator and dealer goals to become a world-class gallerist.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (68, 14, 'https://cf.geekdo-images.com/n4J5LebMj0DQD016qW7ABw__original/img/KDP4s1942J-T52OEytY2VpUWZC0=/0x0/filters:format(jpeg)/pic3184103.jpg', true, 4, 2, 'Mechs vs. Minions', 209010, 120, 'Mechs vs. Minions is a cooperative tabletop campaign set in the world of Runeterra. Players take on the roles of four Yordles who pilot mechs to fight against an army of minions. With modular boards, programmatic command lines, and a story-driven campaign, each mission will test your teamwork, programming, and piloting skills. The game includes ten unique missions that each take about 60-90 minutes to complete, along with various components such as game boards, command lines, miniatures, decks, timers, and more.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (69, 12, 'https://cf.geekdo-images.com/GlCmHjdLwv3SqSjv52ToGw__original/img/5vNCdK9mQ0szo_SwSeVQUWnirZ8=/0x0/filters:format(jpeg)/pic6966955.jpg', true, 4, 2, 'Grand Austria Hotel', 182874, 120, 'In Grand Austria Hotel, players compete to turn their café into a famous hotel in bustling Viennese modern age. By hiring staff, fulfilling guest requests, and gaining the emperor''s favor, players aim to create the most successful hotel. With different actions and bonuses, players must make strategic decisions to win the game, as each playthrough offers new tactics and strategies with its 116 different cards and unique set-up.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (70, 17, 'https://cf.geekdo-images.com/LenzJBOHboAGU0cUIqAZPQ__original/img/-dnx4zkvdOn3CMVHpXIfESazj40=/0x0/filters:format(jpeg)/pic2931007.jpg', true, 4, 1, 'Kingdom Death: Monster', 55690, 180, 'Kingdom Death: Monster is a cooperative tabletop game where players control a settlement in a nightmarish world. Players will fight monsters, craft gear, and develop their settlement to ensure survival. The game features a campaign system with a series of hunts, showdowns, and settlement phases, as well as a monster AI system that provides variable encounters. Players will also have to carefully select and arrange gear cards, and make choices that shape the story events throughout the campaign.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (71, 14, 'https://cf.geekdo-images.com/2ewHIIG_TRq8bYlqk0jIMw__original/img/cassW39WF2QrPImJF59efADAmM0=/0x0/filters:format(jpeg)/pic3738560.jpg', true, 2, 2, 'Android: Netrunner', 124742, 45, 'Welcome to New Angeles, home of the Beanstalk. NBN proudly offers comprehensive streaming in music, news, sitcoms, and more in this cyberpunk future. Android: Netrunner is an asymmetrical Living Card Game where a megacorporation battles lone runners using ice security programs and powerful hacking abilities. The first player to seven points wins, but be prepared for brain damage and unpredictable outcomes.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (72, 14, 'https://cf.geekdo-images.com/e8D09pfFsUsoX1D81EeoZg__original/img/8_FKwPd8bBL3PFjef8Yjz_sFWSk=/0x0/filters:format(jpeg)/pic4705171.jpg', true, 5, 1, 'Cthulhu: Death May Die', 253344, 120, 'In Cthulhu: Death May Die, players take on the roles of 1920s investigators who seek to summon and defeat otherworldly beings rather than prevent their arrival. Despite starting the game insane, the ultimate goal is to eliminate Cthulhu. However, the outcome is unpredictable due to the influence of insanity and the game''s dice-based mechanics. Each standalone episode consists of two acts, with the summoning of creatures being the pivotal moment.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (74, 12, 'https://cf.geekdo-images.com/Vf_0TrTfz9yll7rVBvYGsg__original/img/K6dtSMTAibW5f0QhHXo37CeuECc=/0x0/filters:format(jpeg)/pic3029377.jpg', true, 4, 1, 'Agricola (Revised Edition)', 200680, 120, 'In the revised edition of Agricola, players take on the role of 17th century farmers, growing their families and managing resources. The game features improved components, a revised rulebook, and a card selection from the base game and expansions. With a play time of 30 minutes per player, Agricola offers amazing replay value and a special challenge of feeding your growing family while striving for wealth and prosperity.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (75, 12, 'https://cf.geekdo-images.com/-DOqixs8uwKUvvWPKI4f9w__original/img/Vh-DCkTPa8OU45LaJdUywwhiYqE=/0x0/filters:format(jpeg)/pic5261714.jpg', true, 4, 2, 'Race for the Galaxy', 28143, 60, 'The second edition of the game Race for the Galaxy has been updated to improve color blindness and includes new promo homeworlds. In this card game, players build galactic civilizations by playing game cards that represent worlds or developments. Each round, players secretly select a role that corresponds to a phase, allowing them to perform certain actions and earn bonuses.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (76, 14, 'https://cf.geekdo-images.com/d50LceHj6LIafa4S_qIsCg__original/img/4MsKNGm47PU9BGW4i2yamMlRSQ0=/0x0/filters:format(jpeg)/pic3189350.jpg', true, 4, 1, 'Aeon''s End', 191189, 60, 'Aeon''s End is a cooperative deckbuilding game where players defend the underground city of Gravehold from The Nameless and their hordes. Players use gems to transform the energy from breaches into spells and weapons, strategically managing their decks to defend against attacks. With innovative mechanisms like a variable turn order system and strategic planning, players must use their collective abilities and wits to protect Gravehold.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (77, 14, 'https://cf.geekdo-images.com/cnFppsVNOSTJ-W3APQFuTg__original/img/AbcjscBi-x3tVrsJsBhXq2RxLbc=/0x0/filters:format(jpeg)/pic1974056.jpg', true, 6, 2, 'Eclipse: New Dawn for the Galaxy', 72125, 180, 'After a long period of peace, tension and discord have arisen among the seven major species in the galaxy. As the leader of an interstellar civilization, your goal is to explore new star systems, research technologies, and build spaceships in order to achieve victory. With multiple paths to success, you must carefully plan your strategy while considering the actions of other civilizations.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (78, 13, 'https://cf.geekdo-images.com/dmo-WD6HZHVUPrbVHunaTw__original/img/PS9HXrThM8Pepbd-cuA1tX8KCYU=/0x0/filters:format(jpeg)/pic2055255.jpg', true, 4, 2, 'Five Tribes', 157354, 80, 'Cross into the Sultanate of Naqala and compete to gain control of the city-state. Maneuver the Five Tribes using Djinns and strategic moves to secure victory. Designed by Bruno Cathala, this game features a unique twist on worker placement games with wooden meeples already in place.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (79, 12, 'https://cf.geekdo-images.com/DPjV1iI0ygo5Bl3XLNRiIg__original/img/cXqOdM3BLyIeRt0GP3M3V--gF1M=/0x0/filters:format(jpeg)/pic4449526.jpg', true, 4, 2, 'Clank!: A Deck-Building Adventure', 201808, 60, 'Embark on an adventurous journey in the deck-building board game Clank! Sneak into a dragon''s lair to steal valuable artifacts and gather more loot as you explore. Be careful not to make noise or the dragon will be alerted, and you can only claim your rewards if you manage to escape alive. Use skill, swords, and boots to acquire cards, fight monsters, and navigate the board. The ultimate goal is to retrieve an Artifact token and accumulate enough points to become the Greatest Thief in the Realm.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (80, 12, 'https://cf.geekdo-images.com/3piN3YX4WmBRuIvbEG2Ygg__original/img/9it-9V5tij-EnowN_pLXQgl6k7c=/0x0/filters:format(jpeg)/pic236169.jpg', true, 4, 2, 'Through the Ages: A Story of Civilization', 25613, 120, 'Through the Ages is a civilization building game where players strategically manage resources, technologies, leaders, wonders, and military to create the best civilization. The game spans various ages, from antiquity to the modern age, and features card drafting as a primary mechanic. Balancing advancements in technology and military strength is crucial, as weakness in any area can be exploited by opponents. The player with the most culture at the end of the modern age wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (81, 13, 'https://cf.geekdo-images.com/0w8go6cfvlpgJKydHUkDtA__original/img/322N77t8F7uTev-U_WCqX5uqmEE=/0x0/filters:format(png)/pic2225968.png', true, 2, 1, 'Fields of Arle', 159675, 120, 'Fields of Arle is a farming game created by Uwe Rosenberg, where one to two players live as farmers in the small town of Arle. The game takes you through four and a half years of prosperity, allowing you to farm flax, expand your land, and ship goods to increase your Travel Experience and earn points. With multiple paths to success, work the land and compete with others to make your fortune.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (82, 14, 'https://cf.geekdo-images.com/wW5xjgBJcFyLaEWZwrYuKA__original/img/72kzn6t41ZxLEr3GFd8ir6hDdH4=/0x0/filters:format(jpeg)/pic4583626.jpg', true, 4, 1, 'Teotihuacan: City of Gods', 229853, 120, 'In Teotihuacan: City of Gods, players travel back in time to the Mesoamerican city and compete to become the builder of the Pyramid of the Sun. With worker dice, players strategize, gain favors from gods, and accrue wealth as they navigate the modular board. The game is played in three eras, with points awarded based on player''s efforts and ability to feed their workforce.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (83, 14, 'https://cf.geekdo-images.com/BfEHqHQAvZLbRX7y7e9TWg__original/img/h2NEwLqn5mNkcEfMdP_8oMWa9KE=/0x0/filters:format(jpeg)/pic5617866.jpg', true, 4, 2, 'Beyond the Sun', 317985, 120, 'Beyond the Sun is a space civilization game where players compete to be the leading faction in economic development, science, and galactic influence. The game is played over multiple rounds as players research technologies, improve their economy, control and colonize systems, and complete achievements. Players take turns selecting action spaces, conducting production phases, and researching new technologies to advance their faction.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (84, 12, 'https://cf.geekdo-images.com/-hk8f8iGk_DyWyMrfiPBkg__original/img/GWNOhno3y7g0XE00VacKaRxoczk=/0x0/filters:format(jpeg)/pic1116080.jpg', true, 5, 2, 'Lords of Waterdeep', 110327, 120, 'Waterdeep is a strategic board game for 2-5 players set in the legendary city of Waterdeep. As one of the masked Lords of Waterdeep, players recruit adventurers to complete quests, expand the city, and gain points and resources. Through careful planning, negotiation, and trickery, players compete to earn the most points and become the ultimate ruler of Waterdeep.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (85, 14, 'https://cf.geekdo-images.com/sy89BiuZXfbSnG7Cag9tBQ__original/img/uArsqbWyUALa982zGWsy8QQWnjE=/0x0/filters:format(png)/pic5902073.png', true, 4, 1, 'Obsession', 231733, 90, 'In Obsession, players take on the role of the head of a troubled family estate in Victorian England. They must improve their estate by acquiring building tiles, hosting social activities, and managing their staff to gain wealth, reputation, and connections among the elite families. The game features a unique worker placement mechanic and a competitive courtship for the most eligible individuals in the county, with the player who meets the objectives and accumulates victory points winning the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (86, 12, 'https://cf.geekdo-images.com/n1G7_aWToLAAB7Mqt8iwyA__original/img/VQddnfi_QO0r4GmUU3M8_-nuO3Q=/0x0/filters:format(png)/pic2461346.png', true, 4, 2, 'The Voyages of Marco Polo', 171623, 100, 'In The Voyages of Marco Polo, players recreate Marco Polo''s journey to China in the 13th century. Each player takes on a different historical character with a special power. They roll dice, collect resources, acquire contracts, and travel on the board to reach Beijing. Trading posts in cities provide extra actions and resources. The game ends after five rounds, with players earning victory points for trading posts in Beijing, fulfilling orders, and having trading posts in secret city cards.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (87, 14, 'https://cf.geekdo-images.com/FbFnaTx3aT5dM18K_bR_Pg__original/img/TB2dGHOnuUr54jQYIJZDIMilOJE=/0x0/filters:format(jpeg)/pic3165731.jpg', true, 4, 1, 'Robinson Crusoe: Adventures on the Cursed Island', 121921, 120, 'Robinson Crusoe: Adventures on the Cursed Island is a survival game where players take on the roles of shipwreck survivors on a deserted island. They must build shelter, find food, fight beasts, and protect themselves from the elements. With multiple scenarios and hundreds of cards, players decide the direction of the game and strive to uncover the island''s secrets.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (88, 14, 'https://cf.geekdo-images.com/oeygRZntjNUJWvc8SxDfww__original/img/U-WjAGlPXS_p89z4R0V4b8IADxA=/0x0/filters:format(jpeg)/pic784193.jpg', true, 6, 2, 'Dominant Species', 62219, 240, 'In Dominant Species, players take on the roles of different animal classes during a time of great ice age. They must adapt to the changing earth by strategically placing action pawns and dominating different terrain tiles to gain powerful card effects. The player with the highest victory points at the end of the game becomes the Dominant Species.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (89, 10, 'https://cf.geekdo-images.com/35h9Za_JvMMMtx_92kT0Jg__original/img/jt70jJDZ1y1FWJs4ZQf5FI8APVY=/0x0/filters:format(jpeg)/pic7149798.jpg', true, 7, 2, '7 Wonders', 68448, 30, 'Lead one of the 7 great cities of the Ancient World in 7 Wonders. Collect resources, develop trade routes, and assert military dominance to build your city and create an architectural marvel. During each of the three ages, players choose cards to build their civilization, with some cards providing immediate effects and others offering bonuses or upgrades. Keep an eye on your neighbors and strategize to outwit and overpower them. Suitable for 3-7 players, with a 2-player variant included.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (90, 13, 'https://cf.geekdo-images.com/NItjtztEUAKOpaTWZddu9A__original/img/fr-JmerGdiEj6r9UM7F6laogGF0=/0x0/filters:format(jpeg)/pic5744928.jpg', true, 4, 1, 'The Search for Planet X', 279537, 75, 'The Search for Planet X is a game where players act as astronomers searching for a hidden planet on the edge of the solar system. Using observations and deductions, players mark down information about the objects'' locations on their deduction sheets. The game combines the thrill of discovery with puzzle-solving and competition, challenging players to be the first to find Planet X.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (91, 14, 'https://cf.geekdo-images.com/aAwBzPzta4joKfFZt05hCw__original/img/e1HfD3XIvObNuGH6rq7kUSK-5aQ=/0x0/filters:format(jpeg)/pic4385726.jpg', true, 4, 1, 'Tainted Grail: The Fall of Avalon', 264220, 120, 'Tainted Grail: The Fall of Avalon is a solo or cooperative adventure game for 1-4 players. It combines Arthurian legends and Celtic mythology in a unique way, allowing players to impact the game world through deep decision-making and branching storylines. With challenging encounters, character development, and a rich, non-linear narrative, Tainted Grail offers an immersive and exciting gameplay experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (92, 14, 'https://cf.geekdo-images.com/OYne8uBCHv5oEgRfpOrV0A__original/img/jcTlhpYG2496xQtuQ277bFTIldY=/0x0/filters:format(jpeg)/pic2648303.jpg', true, 4, 1, 'The 7th Continent', 180263, 1000, 'In The 7th Continent, a solo or cooperative exploration board game, you embark on a perilous journey to lift a terrible curse on the newly discovered seventh continent. Inspired by the Fighting Fantasy book series, you navigate through treacherous terrain and encounter various events, utilizing your wit and survival skills. It offers a unique gameplay experience, lasting for many hours, and includes a convenient saving system to allow players to pause and resume their adventure.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (93, 12, 'https://cf.geekdo-images.com/GO282hlXR3RiknU5W4GZjg__original/img/TCTXvYRQGWMAngXuHZcIXRknfpc=/0x0/filters:format(png)/pic5373572.png', true, 5, 2, 'El Grande', 93, 120, 'In this award-winning game, players compete as influential lords in medieval Spain, striving to control various regions. Players draft colored cubes representing knights into their courts, then strategically move them onto the board to gain control. After every third round, regions are scored, with the player accumulating the most points after nine rounds being crowned the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (94, 12, 'https://cf.geekdo-images.com/OAX7HfOz-9N60StgADzd0g__original/img/x2Y_DYWrzzleMETfnu7Jm3R8jPo=/0x0/filters:format(png)/pic3781944.png', true, 5, 1, 'Architects of the West Kingdom', 236457, 80, 'Architects of the West Kingdom is a strategic game set in the Carolingian Empire. Players compete to impress the King by constructing landmarks and advancing work on the cathedral. The aim is to have the most victory points at the end of the game, but players must make moral decisions and avoid falling into corruption.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (95, 14, 'https://cf.geekdo-images.com/5Q2w2rFJiFI_uV89KP6ECg__original/img/MYuRAKLRAGlIQTph2XKWjzqT7sQ=/0x0/filters:format(jpeg)/pic354500.jpg', true, 6, 3, 'Battlestar Galactica: The Board Game', 37111, 180, 'Battlestar Galactica: The Board Game is an exciting game where players take on the roles of characters from the TV show. With their own unique abilities and weaknesses, players must work together to survive and uncover the secret Cylon agents among them. The game involves collecting skill cards, overcoming crises, and managing resources while trying to reach Earth. It is a semi-cooperative game for 3-6 players ages 10 and up, with each game lasting 2-3 hours.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (96, 12, 'https://cf.geekdo-images.com/HbfgxDJZQnNEZQKvpmJxQg__original/img/HsrtfanhKfXX07zNEm-xZpSbxyY=/0x0/filters:format(jpeg)/pic2278942.jpg', true, 6, 2, 'Keyflower', 122515, 120, 'Keyflower is a strategic game for 2-6 players that takes place over four seasons. Players use colored workers to bid for tiles and generate resources, skills, and additional workers. Throughout the game, players must strategize and utilize their resources, transport, and workers to generate the most victory points and win the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (97, 12, 'https://cf.geekdo-images.com/hHZWXnUTMYDd_KTAM6Jwlw__original/img/lL6QJ69UmZT7CeljUcjyA47b-e0=/0x0/filters:format(jpeg)/pic3759421.jpg', true, 8, 3, 'Decrypto', 225694, 45, 'Decrypto is a team-based game where players try to interpret coded messages from their teammates and crack the codes of the opposing team. Each team has their own screen with four numbered pockets, hiding words from the other team. Rounds involve giving clues to match a numbered code, earning marks of success or failure, and the game is won by collecting either two white marks or two black marks.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (150, 12, 'https://cf.geekdo-images.com/k7lG683LBZdvFyS-FH-MpA__original/img/hEqkeRnhyNLxeryIIdOnAHdItfo=/0x0/filters:format(png)/pic6746812.png', true, 5, 2, 'Ra', 12, 60, 'Ra is an Ancient Egyptian-themed auction and set-collection game. Players use bidding tiles to purchase lots of tiles and earn points. The game is fast-paced and offers strategic gameplay, spanning 1500 years in less than an hour.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (98, 14, 'https://cf.geekdo-images.com/6QE8mnPFmauoj2THsyUYPA__original/img/6bZdn1SNbpIqU7n5pwtncvdNE_U=/0x0/filters:format(jpeg)/pic4739757.jpg', true, 4, 2, 'Inis', 155821, 90, 'Inis is a game set in Celtic history where players compete to become the King of the Island. There are three ways to achieve victory: having the most clan figures in territories with at least six opponents'' clans, having clans present in at least six different territories, or having clans present in territories with at least six sanctuaries. With careful drafting, hand management, and a balance of power, players can claim the title of King in this strategic game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (99, 12, 'https://cf.geekdo-images.com/yC7nOSc1x5PT-oNnh6TEcQ__original/img/ugJgRdFpYSsb5Pj1NDk76q6LKTo=/0x0/filters:format(jpeg)/pic1638795.jpg', true, 5, 2, 'Caylus', 18602, 150, 'In Caylus, players take on the role of master builders in the Kingdom of France. They must construct the King''s castle and develop the surrounding city to earn prestige points and gain favor with the King. By placing workers in various buildings, players can gather resources, build or upgrade buildings, and contribute to the construction of the castle. The game is influenced by the choices players make regarding which buildings are active and how they manipulate the movement of markers, which also determine the end of the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (100, 12, 'https://cf.geekdo-images.com/hXdfAhgZ8OeyWLunt36wlw__original/img/f5wjooKB2dGPBlBzws5cDsuuJ-k=/0x0/filters:format(jpeg)/pic3578101.jpg', true, 4, 2, 'Raiders of the North Sea', 170042, 80, 'Raiders of the North Sea is a Viking-themed game where players aim to impress a Chieftain by raiding settlements. Players assemble a crew, collect provisions, and journey north to plunder gold, iron, and livestock. Victory points are acquired through raiding settlements, taking plunder, and making offerings, with the game ending when certain conditions are met.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (101, 12, 'https://cf.geekdo-images.com/VceWk5QVkgIp6rWDl5qHKQ__original/img/8lLnp9nod-NhjaoUobcZ3vvb7Mg=/0x0/filters:format(jpeg)/pic2611318.jpg', true, 4, 2, 'Mombasa', 172386, 150, 'In Mombasa, players invest in chartered companies and expand their trading posts across Africa to earn money. The game features a unique hand-mechanism and rotating display that determine players'' actions. After seven rounds, scores are tallied and the player with the highest score wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (102, 13, 'https://cf.geekdo-images.com/OGOmpi0GgwOwH2y28QgkuA__original/img/VHMKsFZvoK2KKGZiLyoKZb_YhNQ=/0x0/filters:format(jpeg)/pic460011.jpg', true, 6, 2, 'Dominion: Intrigue', 40834, 30, 'Dominion: Intrigue is a deck-building game where players start with identical decks and strive to gain victory points by efficiently constructing their decks. The game features secret plots and adds new rules for playing with up to 8 players or combining with the basic Dominion game. It includes 25 new Kingdom cards and integrates with the Dominion series.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (103, 14, 'https://cf.geekdo-images.com/9gob752VqEklQFV_gz16hw__original/img/ivQUea2qrHp2TDLM4i7gp1123vQ=/0x0/filters:format(png)/pic2585510.png', true, 4, 2, 'Trickerion: Legends of Illusion', 163068, 180, 'Trickerion is a competitive Euro-style strategy game set in a fictional world inspired by late 19th-century urban life and culture. Players take on the roles of rival stage illusionists, competing for fortune and fame in a competition held by a legendary magician. Using worker placement and simultaneous action selection mechanisms, players obtain blueprints and components for magic tricks, expand their teams, and set up performances in various locations around the city. The game features a variety of tricks, character abilities, and optional rule modules to enhance strategic depth.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (104, 12, 'https://cf.geekdo-images.com/soAzNVWglCdVBacNjoCTJw__original/img/HClVZy9phFP-NPte4UBQpclsaHU=/0x0/filters:format(jpeg)/pic2338267.jpg', true, 4, 2, 'Tigris & Euphrates', 42, 90, 'Tigris & Euphrates is a highly regarded game by Reiner Knizia set in the ancient fertile crescent. Players use four different leaders to collect victory points in farming, trading, religion, and government. Conflict arises when civilizations connect on the board, and the game encourages players to avoid becoming overly specialized.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (105, 12, 'https://cf.geekdo-images.com/fBIe-aEPtJKC-ceNlspK6A__original/img/otcIVdG4ebZmK1SjUtzma18JtJ4=/0x0/filters:format(jpeg)/pic750583.jpg', true, 4, 1, 'Troyes', 73439, 90, 'In Troyes, players manage their population and hand of cards to recreate four centuries of history in the Champagne region of France. Players can offer cash to opponents to get more fame and make their underlings work on the cathedral, combat misfortune, and complete tasks below their family''s stature. Some editions released in 2016 or later include a solo variant and bonus cards.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (106, 12, 'https://cf.geekdo-images.com/8G6GkImjt0F_L5aYb8fmtQ__original/img/dOiPu4wGisyuw3KxDketwCSpOfY=/0x0/filters:format(jpeg)/pic3175535.jpg', true, 4, 2, 'Lorenzo il Magnifico', 203993, 120, 'Lorenzo il Magnifico is a game where players take on the role of noble heads of families during the Italian Renaissance. They compete to gain victory points by sending family members to different areas of the city to obtain resources, conquer territories, and activate card effects. The game is divided into three periods, and at the end, the player with the most VP wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (107, 14, 'https://cf.geekdo-images.com/oxdsZKIX5_3ovDo38EIaeg__original/img/vxK08kQAEn5qPbejK2CUTyWOsig=/0x0/filters:format(jpeg)/pic1872452.jpg', true, 8, 1, 'Eldritch Horror', 146021, 240, 'In Eldritch Horror, players work together to solve mysteries and protect the world from ancient evil. As globetrotting investigators, they must gather clues and battle foul monsters to prevent the approaching horror from overwhelming humanity. With unique investigators, tokens, and cards, each game offers an epic adventure that spans the globe.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (108, 14, 'https://cf.geekdo-images.com/1XA2-niwTBa0HEB7hOz3Dw__original/img/MIJgG_K2IS1QT2mE0dyRFiWLSpg=/0x0/filters:format(jpeg)/pic4530974.jpg', true, 5, 1, 'The Lord of the Rings: Journeys in Middle-Earth', 269385, 120, 'Embark on your own adventures in The Lord of the Rings: Journeys in Middle-Earth, a cooperative board game for 1-5 players. Battle foes, make choices, and fight against evil in a thrilling campaign set in the iconic world of J.R.R. Tolkien. Explore dynamic landscapes and survive challenges with the help of the companion app, and create your own legend in the history of Middle-Earth.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (109, 14, 'https://cf.geekdo-images.com/MHeQQrbauUVsvh2QF6PMog__original/img/LfjXNSzbVuSmMfcRYwGcuZwFHZo=/0x0/filters:format(jpeg)/pic4128153.jpg', true, 6, 3, 'Twilight Imperium: Third Edition', 12493, 240, 'Twilight Imperium Third Edition is an epic empire-building game where players take on the roles of ancient galactic civilizations, competing for the imperial throne through warfare, diplomacy, and technology. With a dynamic game map, unique alien races, and strategic dimensions inspired by popular games like Puerto Rico and CATAN, this well-rounded and familiar experience offers fast-paced gameplay with very little downtime. Re-implemented in the Twilight Imperium series, it has also been expanded and improved upon in later editions.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (111, 13, 'https://cf.geekdo-images.com/B0oEVgeqs4_rT8oyb1_Juw__original/img/EnBmUkDKSbcBusI6nVRczadYs0M=/0x0/filters:format(jpeg)/pic1772936.jpg', true, 4, 2, 'Russian Railroads', 144733, 120, 'In Russian Railroads, players compete to build the largest and most advanced railway network. Use workers strategically to develop tracks, improve efficiency, and overcome challenges. Score victory points by advancing track lines and factories. The player with the most points wins after 7 rounds.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (112, 8, 'https://cf.geekdo-images.com/wLW7pFn0--20lEizEz5p3A__original/img/B1j7R-slq8wxgC71Sg1F7S1s7Kg=/0x0/filters:format(jpeg)/pic2270442.jpg', true, 2, 2, 'Patchwork', 163412, 30, 'In Patchwork, players compete to build the best patchwork quilt on a 9x9 game board. Each turn, players can either purchase a patch or pass. Patches are added to the game board and players earn buttons based on the buttons depicted on their board. The game ends when both players reach the center of the time track and scoring takes place based on buttons and empty squares on the board.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (113, 12, 'https://cf.geekdo-images.com/djdeCr__iNV5rMnGnxNiaQ__original/img/-pxFp0FHDLLZK_hMT5kHG7IAQ7M=/0x0/filters:format(png)/pic4336095.png', true, 4, 2, 'Trajan', 102680, 120, 'In Trajan, players compete to increase their influence in ancient Rome through political influence, trading, military dominion, and other aspects of Roman culture. The game utilizes a Mancala-style mechanism, giving players six actions to choose from each turn. Players aim to acquire victory points by meeting the demands of the public, acquiring influence in the Senate, and gaining control over regions in Europe.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (114, 14, 'https://cf.geekdo-images.com/iwevA6XmiNLHn1QnGUucqw__original/img/pKJ1XyLN4hvHim5eRj2VJHijv1E=/0x0/filters:format(jpeg)/pic3880340.jpg', true, 5, 3, 'Rising Sun', 205896, 120, 'The forgotten Kami have returned from the underworld displeased with the current Shōgun. In Rising Sun, players compete to reclaim Nippon''s lands and restore honorable traditions. Through diplomacy, negotiation, and battles, players must accumulate Victory Points and navigate alliances to become the victorious Shōgun.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (115, 14, 'https://cf.geekdo-images.com/H2st1bZH4O0FM46NhKMqyQ__original/img/wR0VHD6Svlp34wxKN6OHV4YZZgM=/0x0/filters:format(jpeg)/pic3430893.jpg', true, 4, 1, 'Aeon''s End: War Eternal', 218417, 60, 'Gravehold, the last stronghold of The World That Was, is under attack by The Nameless. In War Eternal, a standalone game compatible with Aeon''s End, players must defend Gravehold using unique abilities, spells, and dynamic characters. With innovative mechanisms and the option to combine with other Aeon''s End content, War Eternal offers a unique and chaotic game experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (116, 13, 'https://cf.geekdo-images.com/j6iQpZ4XkemZP07HNCODBA__original/img/96COuakNiLRrjDLc1sM4Zxsw4WE=/0x0/filters:format(jpeg)/pic394356.jpg', true, 4, 2, 'Dominion', 36218, 30, 'In Dominion, you take on the role of a monarch who wants to expand their kingdom by claiming unclaimed lands. You must race against other monarchs to acquire as much land as possible, while also hiring minions, constructing buildings, and managing your treasury. With 500 cards to choose from and different combinations each game, Dominion offers immense variety and strategic gameplay.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (117, 12, 'https://cf.geekdo-images.com/0qxz9DRRhsJrIW1TJ-kz0w__original/img/PzsJsCLfGGo4IG3v43VPvH_etPw=/0x0/filters:format(jpeg)/pic5710736.jpg', true, 4, 1, 'Carnegie', 310873, 120, 'Carnegie is a game inspired by the life of Andrew Carnegie, who became one of the richest men in the world through his role in the rise of the US steel industry. In the game, players will recruit employees, expand their businesses, invest in real estate, produce and sell goods, and create transport chains across the United States. The goal is to build the most prestigious company and accumulate victory points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (118, 8, 'https://cf.geekdo-images.com/AuAheDzhkLufiZFE9JaRNA__original/img/pKq-l1Ms78olHwMbZYMa_x6m-fM=/0x0/filters:format(jpeg)/pic4781085.jpg', true, 4, 1, 'The Isle of Cats', 281259, 90, 'The Isle of Cats is a competitive card-drafting, polyomino cat-placement board game for 1-4 players (6 with expansions). As citizens of Squalls End, you must rescue as many cats as possible from The Isle of Cats before the arrival of Lord Vesh. Explore the island, rescue cats, find treasures, befriend Oshax, and study ancient lessons to score points and win the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (119, 13, 'https://cf.geekdo-images.com/gBPzZsgm9nrUYVfdK216NA__original/img/eQEmKYEWVoNvEx6PZYZv13275io=/0x0/filters:format(png)/pic4557340.png', true, 6, 1, 'Age of Steam', 4098, 120, 'In Age of Steam, players take on the role of U.S. railroads as they compete to build extensive track networks and powerful locomotives. The game features challenging decisions such as choosing routes for the best returns on costs, beating opponents to lucrative shipments, and managing aggressive creditors. With intense competition and constant involvement in every phase, Age of Steam offers a unique and varied experience every time.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (120, 14, 'https://cf.geekdo-images.com/JuyN9UOenAG_BlO-APPpdw__original/img/_0ZWOmqFvL5UybNWgPIyioib3mQ=/0x0/filters:format(jpeg)/pic2649446.jpg', true, 4, 1, 'Vinhos: Deluxe Edition', 175640, 135, 'Vinhos is a trading and economic game where players compete to be the best winemaker in Portugal. Develop vineyards, produce quality wines, and participate in trade fairs to earn money or victory points. The Vinhos Deluxe Edition includes new art, improved rules, and a simplified version of the game with added features like new regions, estates, and characters.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (121, 12, 'https://cf.geekdo-images.com/haDQw7X8Z0VOItfow9eDtg__original/img/Gq7eW_IFnUgIFcWcZSbWihAzdTs=/0x0/filters:format(jpeg)/pic3720843.jpg', true, 4, 2, 'Clank! In! Space!: A Deck-Building Adventure', 233371, 90, 'In Clank! In! Space!, you and your fellow thieves must sneak aboard Lord Eradikus'' flagship, hack into its command module, and steal his prized artifacts. Along the way, you can recruit allies and collect loot, but be careful not to make too much noise and attract the attention of Lord Eradikus. This game is a deck-building adventure where players use cards to navigate the spaceship, attack, acquire new cards, and strategically make noise.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (122, 10, 'https://cf.geekdo-images.com/GLXfUI72Qph2136CxWIJww__original/img/SLvKZhmr5l30xLGWO2TO3Lr_wvs=/0x0/filters:format(png)/pic7648372.png', true, 4, 2, 'The Quest for El Dorado', 217372, 60, 'In The Quest for El Dorado, players lead expeditions through the jungles of South America, seeking the legendary land of gold. They recruit team members and employ different strategies to reach the golden border first and claim the riches of El Dorado. Success depends on tactical decisions and outsmarting opponents.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (123, 12, 'https://cf.geekdo-images.com/3IkzYcplGfFtVrd3syClHg__original/img/RqYxJUdNXPmHUWrlADIbTf9Bi_g=/0x0/filters:format(jpeg)/pic839090.jpg', true, 5, 2, 'Hansa Teutonica', 43015, 90, 'In this trading game, players aim to earn victory points by establishing a network of offices, controlling cities, collecting bonus markers, and benefiting from other traders'' use of their controlled cities. To do this, they can build offices, gain skill improvements, and strategically connect lines between cities with their pawns. These skill improvements unlock various advantages such as more VP, increased action points, additional pawns, and the ability to place special pawns. Originally known as Wettstreit der Händler, this game was first introduced at the Hippodice competition.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (124, 8, 'https://cf.geekdo-images.com/752GFSJLkyDZikThRM6gqQ__original/img/SMtMEB-rB_l3p_tmTYzAYGx_IkI=/0x0/filters:format(png)/pic3000850.png', true, 5, 2, 'Pandemic: Iberia', 198928, 45, 'Welcome to Pandemic Iberia, set in 1848 on the Iberian Peninsula. Take on various roles to find cures for diseases such as malaria, typhus, yellow fever, and cholera. Travel by carriage, boat, or train to help the Iberian populace, while also distributing purified water and developing railways to slow the spread of diseases. This game offers a unique historical setting during the construction of the first railroad in the Iberian Peninsula.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (125, 14, 'https://cf.geekdo-images.com/F_KDEu0GjdClml8N7c8Imw__original/img/gcX_EfjsRpB5fI4Ug4XV73G4jGI=/0x0/filters:format(jpeg)/pic2582929.jpg', true, 8, 2, 'Codenames', 178900, 15, 'Codenames is a party game where players must solve puzzles. Two teams, red and blue, have team leaders who must guide their teams to victory. Players must use word prompts to identify cards of their team''s color and find all the cards of their color to win.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (126, 14, 'https://cf.geekdo-images.com/3edsEXwH21uaO8HXXdD1Dg__original/img/O-03BIpt7yhPbsffYjDwZc68zbQ=/0x0/filters:format(png)/pic7745916.png', true, 4, 2, 'Yokohama', 196340, 90, 'In YOKOHAMA, players take on the role of merchants in the Meiji period''s flourishing trade city. Players must build stores, expand their sales channels, and learn new techniques to gain fame and success. They must also respond to trade orders from abroad in order to thrive in this era of cultural integration and modernization.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (127, 14, 'https://cf.geekdo-images.com/WlvyznYhlYNXMtMbKKu1oA__original/img/AQ1P6PKw7ZW-XTOAFKhCwsnfP0I=/0x0/filters:format(jpeg)/pic2471359.jpg', true, 4, 2, 'Forbidden Stars', 175155, 180, 'Forbidden Stars is a strategy game where players take command of different factions and compete to establish dominance in the Herakon Cluster. Each faction has unique armies and play styles, and the goal is to claim key objectives scattered throughout the cluster while defending against opponents. The gameplay consists of three phases - planning, operations, and refresh - that require a balance of bluffing and tactical military action.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (128, 10, 'https://cf.geekdo-images.com/VJwOnMF5vwJg2Yaq6ozn3Q__original/img/iEgr80D4UtP0x_WQUYSB60JwGJ0=/0x0/filters:format(jpeg)/pic2869714.jpg', true, 4, 2, 'Champions of Midgard', 172287, 90, 'Champions of Midgard is a Viking-themed worker placement game where players lead Viking clans to defend a harbor town from mythical creatures. By defeating the creatures, players earn glory and the favor of gods. Players collect resources, send warriors on journeys, and use custom dice to battle enemies and gain victory.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (129, 14, 'https://cf.geekdo-images.com/qJQXn0kvzvHBKWzSQN7vpw__original/img/NAq9RfDsv-qPEsco3W0ES8ofHEA=/0x0/filters:format(jpeg)/pic4742944.jpg', true, 5, 1, 'Dwellings of Eldervale', 271055, 150, 'Dwellings of Eldervale is an epic worker placement game where players control factions vying for dominance over elemental realms. The game combines worker placement, area control, engine building, and unique worker units. Players can adventure, battle, and grow in power in order to shape Eldervale to their vision and ultimately win by achieving elemental dominance.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (130, 13, 'https://cf.geekdo-images.com/nhoyoLpYPC2b3UiJ5kJ0yg__original/img/LoETJCbq1HpSlhPLdci3qJekpwA=/0x0/filters:format(jpeg)/pic1473629.jpg', true, 5, 2, 'Roll for the Galaxy', 132531, 45, 'Roll for the Galaxy is a dice game for 2-5 players where you build space empires. Use your dice to develop technologies, settle worlds, and ship goods. The player who manages their workers and builds the most prosperous empire wins. This dice version offers a new journey through the Galaxy with the same feel as the original game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (131, 10, 'https://cf.geekdo-images.com/mF2cSNRk2O6HtE45Sl9TcA__original/img/_KNgPoC_4l7iN0ntbFILexOJpfc=/0x0/filters:format(jpeg)/pic4852372.jpg', true, 5, 1, 'PARKS', 266524, 60, 'PARKS is a US National Parks themed game where players take on the role of two hikers exploring trails throughout the year. They collect memories represented by resource tokens and can trade them in to visit National Parks. The game features changing trails, limited resources, and strategic decision-making.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (132, 10, 'https://cf.geekdo-images.com/GifbnAmsA4lfEcDkeaC9VA__original/img/YzJORdIDNza8kmrY6KEU_h2p2Wo=/0x0/filters:format(png)/pic4397932.png', true, 100, 1, 'Cartographers', 263918, 45, 'Queen Gimnax has tasked you with mapping and reclaiming the northern lands for the Kingdom of Nalos. Compete with other players to earn reputation stars by fulfilling the queen''s edicts and strategically drawing your lines to minimize the influence of the Dragul. The player with the most reputation stars at the end of winter will be declared the greatest cartographer in the kingdom.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (133, 8, 'https://cf.geekdo-images.com/S3ybV1LAp-8SnHIXLLjVqA__original/img/IsrvRLpUV1TEyZsO5rC-btXaPz0=/0x0/filters:format(jpeg)/pic1534148.jpg', true, 4, 2, 'Pandemic', 30549, 45, 'In Pandemic, players must work together as disease-fighting specialists to treat disease hotspots, research cures, and prevent outbreaks of four deadly plagues. Throughout the game, players must strategically use their actions to travel between cities, treat infections, build research stations, and discover the cures. The game includes new characters, the Contingency Planner and Quarantine Specialist, and the objective is to cure the four diseases before time runs out or the diseases spread beyond recovery.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (134, 12, 'https://cf.geekdo-images.com/gcALy45JfcjyKUn6T3mBIQ__original/img/YLSAurBSWEGi3PHsuQWi8St_0Yg=/0x0/filters:format(jpeg)/pic4422847.jpg', true, 4, 2, 'Res Arcana', 262712, 60, 'In Res Arcana, players assume the role of mages using essences such as Life, Death, Elan, Calm, and Gold to craft artifacts and summon dragons. The game consists of 4-6 rounds, where players collect essences, take actions, pass to gain advantages, and earn victory points. The player with the most victory points at the end of the game wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (135, 12, 'https://cf.geekdo-images.com/wHg4fOf48cs1kw1PDOk1tQ__original/img/St4ABQhpauYiq4Mt6-f38JtLOjM=/0x0/filters:format(jpeg)/pic3958793.jpg', true, 2, 2, 'Targi', 118048, 60, 'In Targi, players take on the role of Tuareg tribe leaders, trading goods to obtain gold and benefits to expand their family. By strategically placing meeples on the board and collecting cards, players aim to score victory points and create advantageous combinations for their tribe. The player with the highest victory point total at the end of the game emerges as the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (136, 13, 'https://cf.geekdo-images.com/ztNshEv3Fsm46HCJaEOFPw__original/img/SP4ItZ27dLweUPbz_bF7C-P7GtA=/0x0/filters:format(png)/pic2241156.png', true, 4, 2, 'Alchemists', 161970, 120, 'In Alchemists, two to four players compete to discover the secrets of alchemy. The game is played over six rounds, and players earn points by publishing correct theories and gaining knowledge through mixing ingredients and testing them. Players can also earn reputation, money, and artifacts, with reputation and artifacts ultimately determining the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (137, 12, 'https://cf.geekdo-images.com/hR5sxxqNY3gjvLwifaqRww__original/img/kaXHOjBJX-URkjTjhQUhFl7E8Ng=/0x0/filters:format(jpeg)/pic3646165.jpg', true, 4, 2, 'Rajas of the Ganges', 220877, 75, 'In Rajas of the Ganges, players compete to develop their estates into wealthy provinces and gain wealth and fame. They must use their dice strategically and plan their worker placements to support the powerful empire of the Great Moguls in 16th century India. Good karma plays a crucial role in achieving success and becoming legendary rulers.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (138, 13, 'https://cf.geekdo-images.com/RmZjhy7_6REp8IaXh9Zqwg__original/img/kRFfkVnQRsBDcbO46TKS6PcT6aE=/0x0/filters:format(jpeg)/pic3979527.jpg', true, 5, 2, 'Kemet', 127023, 120, 'In Kemet, players deploy Egyptian troops and use the powers of the ancient gods to score points through battles and territory invasion. The game is played over two phases, Day and Night, with players taking actions during the Day and gaining Prayer Points and Divine Intervention cards during the Night. As the game progresses, players can acquire power tiles and summon magical creatures to join their armies and provide special powers.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (139, 12, 'https://cf.geekdo-images.com/iTBogxNFa3Ymh-6ST3srsw__original/img/ECgm53fEsB3eDVjZjgeejiZ__Qk=/0x0/filters:format(jpeg)/pic4768766.jpg', true, 2, 2, 'Watergate', 274364, 60, 'In Watergate, players take on the roles of a Journalist and the Nixon Administration. The Nixon Administration tries to complete their presidential term, while the Journalist aims to connect two informants to the President using evidence. The game includes a history of the Watergate scandal.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (140, 13, 'https://cf.geekdo-images.com/ptDZ2tJ6dSNiONAx3HH8Tw__original/img/iu21NR45DwHyFY-UPW0m7hwY_vw=/0x0/filters:format(jpeg)/pic3514298.jpg', true, 8, 1, 'Sherlock Holmes Consulting Detective: The Thames Murders & Other Cases', 2511, 120, 'Experience the thrill of stepping into Sherlock Holmes'' shoes as you navigate the streets of Victorian London in search of mysteries to solve. From the opium dens to the docks, you''ll interview suspects, analyze clues, and piece together the evidence to crack each case. This challenging and immersive game will put your deductive abilities to the test as you compete against opponents and the master sleuth himself.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (141, 12, 'https://cf.geekdo-images.com/EsJqPL5CB_TMt0MYL-lnVg__original/img/QXgamCDRuBoRJyU1PQjeh58Pvr8=/0x0/filters:format(jpeg)/pic1903816.jpg', true, 2, 2, 'Star Realms', 147020, 20, 'Star Realms is a fast-paced deck-building game where players engage in outer space combat. Acquire and use Ships and Bases to generate Trade or Combat and strategically reduce your opponent''s score to win. With multiple decks available and various game modes, Star Realms offers a depth of strategy and replayability for both beginners and advanced players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (142, 12, 'https://cf.geekdo-images.com/aUPeXVwc40xrgud2XeZwyA__original/img/WbCD6iqzBhVucS7uRwYvtku5zD0=/0x0/filters:format(png)/pic5671087.png', true, 4, 1, 'Praga Caput Regni', 308765, 150, 'In Praga Caput Regni, players compete to impress King Charles IV by organizing building projects in medieval Prague. Through strategic actions and resource management, players can expand their wealth, improve their abilities, and construct various landmarks in the city. The player who impresses the king the most will be crowned the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (143, 14, 'https://cf.geekdo-images.com/od4vDTdXGPSVc-kjLqM11w__original/img/sN8TnSkFGCzNM01b_drFxwYnqlM=/0x0/filters:format(jpeg)/pic4651175.jpg', true, 5, 1, 'It''s a Wonderful World', 271324, 60, 'In It''s a Wonderful World, players take on the role of an expanding empire, strategically planning their expansion to outpace their competitors. Through card drafting and engine building, players acquire resources, construct buildings, and increase their production capacity. The game offers an innovative Campaign mode with a storyline and gameplay twists, allowing players to unlock new cards and enhance their base game without destroying any game components.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (144, 13, 'https://cf.geekdo-images.com/YXYqQZ05gC9kNlgX7cbR8g__original/img/D4TCl_FNsC867ww_NNj4VmVUBr0=/0x0/filters:format(jpeg)/pic3913274.jpg', true, 5, 2, 'Endeavor: Age of Sail', 233398, 90, 'Endeavor: Age of Sail is a game where players aim to earn glory for their empires by establishing shipping routes and occupying cities around the world. With a double-sided board, variable starting setups, and exploits to enhance gameplay, players can strategically build their industry, culture, finance, and Influence to expand their reach and engine. This second edition also features updated visuals by the original artist, Josh Cappel.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (145, 8, 'https://cf.geekdo-images.com/ocAuKT9hp99yBY77e4uuPg__original/img/hYCWWUIvO5DnVqx_Ij4JnRIyLHo=/0x0/filters:format(jpeg)/pic5137279.jpg', true, 7, 3, 'Just One', 254640, 60, 'Just One is a cooperative party game where players work together to guess mystery words. Score points by giving unique clues, as identical clues are cancelled. The goal is to achieve a score as close to 13 as possible, with correct answers earning 1 point and incorrect answers resulting in a loss of 2 points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (146, 12, 'https://cf.geekdo-images.com/4XzRDw3VrgNpNfZlzZl66w__original/img/SfU1sNqUtpcvhcdzspDb5M9uSQQ=/0x0/filters:format(png)/pic5608818.png', true, 6, 1, 'Hadrian''s Wall', 304783, 60, 'Hadrian''s Wall is a game where players become Roman Generals tasked with constructing a milecastle and wall to separate the Picts from England. Over six rounds, players must build defenses, attract civilians, and defend against the Picts, aiming to accumulate the most points and become the model Roman citizen. Explore the remains of the forts, towers, and turrets of the real-life UNESCO World Heritage Site.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (147, 10, 'https://cf.geekdo-images.com/elmZegVZ6gp4_5izUgxGQQ__original/img/cR92Dm6yQ3Lgxt1yR81zUb0ooqQ=/0x0/filters:format(jpeg)/pic1632539.jpg', true, 4, 2, 'Stone Age', 34635, 90, 'In Stone Age, players take on the roles of ancient hunters, collectors, farmers, and tool makers living in the challenging "Stone Age" era. They must gather resources such as wood, stone, and gold, trade with other players, and expand their villages to advance civilization. With a combination of strategy and luck, players strategically place their tribe members, activate different areas of the board, and ensure they have enough food to sustain their population.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (148, 17, 'https://cf.geekdo-images.com/jSz_KRUxsjGYitoqx9YH1Q__original/img/nrvJtT-4tYTD0fp3Cr57SKpfMfw=/0x0/filters:format(jpeg)/pic2225180.jpg', true, 5, 1, 'Legendary Encounters: An Alien Deck Building Game', 146652, 60, 'Legendary Encounters: An Alien Deck Building Game is a cooperative card game based on the Alien series movies. Players take on the roles of iconic characters and work together to survive against waves of alien attacks. The game features original art, a full-color game board, and includes 600 cards for an immersive gameplay experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (149, 8, 'https://cf.geekdo-images.com/0K1AOciqlMVUWFPLTJSiww__original/img/O37sCRSJLq4S8EpCxFDNVsNBuxE=/0x0/filters:format(jpeg)/pic66668.jpg', true, 5, 2, 'Ticket to Ride: Europe', 14996, 60, 'Ticket to Ride: Europe is a new train adventure game that takes you across Europe, from Edinburgh to Constantinople and Lisbon to Moscow. With new gameplay elements such as tunnels, ferries, and stations, this game is both simple to learn and enjoyable for both families and experienced gamers. Collect train cards, connect cities on your ticket cards, and earn points to win the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (151, 14, 'https://cf.geekdo-images.com/eT_Atcy_vRJvuUMgYakNrQ__original/img/upZwrb6k8zZMNTwB8VfpI372yuo=/0x0/filters:format(jpeg)/pic6260098.jpg', true, 4, 1, 'Terraforming Mars: Ares Expedition', 328871, 60, 'In Terraforming Mars: Ares Expedition, players control interplanetary corporations aiming to make Mars habitable and profitable. This engine-building game involves investing mega credits into project cards to contribute to the terraforming process and accumulate a high terraform rating and victory points. The game is played in rounds with different activities, including building project cards, taking actions, producing resources, and researching, while players act simultaneously within each phase. The game ends when Mars becomes habitable, and the winner is the player with the most victory points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (152, 13, 'https://cf.geekdo-images.com/ZmXi5BbtoWl58ZnKcUy1vw__original/img/nNHf1qRJCVQx_0p-1zM8QKxcywo=/0x0/filters:format(jpeg)/pic906495.jpg', true, 2, 1, 'The Lord of the Rings: The Card Game', 77423, 60, 'The Lord of the Rings: The Card Game is a cooperative adventure game with up to three heroes of choice. Players send their heroes and allies to complete quests or fight enemies, but must manage their resources and prioritize actions. The game includes three scenarios, famous heroes from Tolkien''s works, and the ability to construct custom decks. It also offers new content regularly through Campaign and Hero Expansions, allowing players to create their own Middle-earth adventures.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (153, 9, 'https://cf.geekdo-images.com/cf0xxkevbwTGF3VUZymKjg__original/img/vkElrRCPJxy7Q3TH9olpq20egA4=/0x0/filters:format(png)/pic6398727.png', true, 5, 2, 'SCOUT', 291453, 15, 'SCOUT is a ladder-climbing game where cards have dual values. Players cannot rearrange their hand and can pass their turn to take a card from the current high set of cards. The goal is to play sets of cards with higher values or scout to earn points and win the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (154, 14, 'https://cf.geekdo-images.com/yNqgVxaqoZepir7Hzkfe-Q__original/img/H6rYWOhYjT_PXTrvHKyX6FEt-vw=/0x0/filters:format(jpeg)/pic1603292.jpg', true, 2, 2, 'Star Wars: X-Wing Miniatures Game', 103885, 45, 'Star Wars: X-Wing Miniatures Game is a tactical ship-to-ship combat game where players control Rebel X-wings and Imperial TIE fighters in fast-paced space combat. With detailed miniatures and multiple scenarios, players select crews, plan maneuvers, and complete missions. The game features unique missions with victory conditions and special rules, customizable options, and a quick-to-learn ruleset that can be expanded with additional ships and characters.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (155, 12, 'https://cf.geekdo-images.com/rc7CQ5l95ZFoKq3iBVqjgg__original/img/3EH__HLlmY7adABsY_HUtAlEMfo=/0x0/filters:format(jpeg)/pic5543526.jpg', true, 4, 2, 'Marco Polo II: In the Service of the Khan', 283948, 120, 'In Marco Polo II: In the Service of the Khan, players embark on epic journeys in the service of the Khan, exploring distant regions of his empire to seek wealth and fame. This standalone game based on The Voyages of Marco Polo introduces new actions, scoring rules, and a valuable new resource: Chinese jade. Retread old paths with a renewed purpose or forge new routes as you continue building the iconic legacy of Marco Polo.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (156, 12, 'https://cf.geekdo-images.com/kMgSC_kRlJWt3KLj5y0BxQ__original/img/t9ccf6x3PQiL5Cqn4-mePKUNC-E=/0x0/filters:format(png)/pic5230695.png', true, 4, 1, 'Viscounts of the West Kingdom', 296151, 90, 'Viscounts of the West Kingdom is a strategic game set during the decline of a once strong King''s reign. Players aim to accumulate the most victory points by constructing buildings, writing manuscripts, and acquiring new land. The game revolves around seeking favor among the people and navigating the shifting power dynamics of the kingdom.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (157, 13, 'https://cf.geekdo-images.com/CxJmNl4wR4InjqyNrMdBTw__original/img/TKQgvl_i6zVYld8Gac8oU-c2ezE=/0x0/filters:format(jpeg)/pic163749.jpg', true, 2, 2, 'Magic: The Gathering', 463, 20, 'In the Magic game, players take on the role of powerful wizards known as planeswalkers, fighting against each other using decks of cards representing spells and creatures. The objective is to reduce the opponent''s life total to zero through dueling and strategic deck construction. With over 25,000 different cards available, players have a wide range of options to choose from in order to build their decks and outsmart their opponents.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (158, 12, 'https://cf.geekdo-images.com/sMRuph3HFUcaSpJZihGZKw__original/img/ehoqoTirtFb5p9Wk2SwWV8nr6ZU=/0x0/filters:format(jpeg)/pic5841874.jpg', true, 4, 2, 'Glen More II: Chronicles', 265188, 120, 'Glen More II: Chronicles is a sequel to Glen More, offering expanded gameplay in which players represent Scottish clan leaders aiming to expand territory and wealth from medieval times to the 19th century. Decision-making is crucial for success, whether it''s building pastures, producing whisky, selling goods, or controlling landmarks. The game consists of four rounds, with scoring phases after each round based on various factors. It features improvements in components, artwork, and allows players to control the end of the game. Eight expansions called "Chronicles" add new gameplay elements, and players can invest in Scottish historical figures for additional strategies.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (159, 10, 'https://cf.geekdo-images.com/PHH9Mreen2sBcxV6ZsE3ew__original/img/pHIJibqf-DmlOQD3CfT_rAjGmc0=/0x0/filters:format(jpeg)/pic1885326.jpg', true, 5, 2, 'Istanbul', 148949, 60, 'In Istanbul, compete with other merchants to collect rubies by navigating the bustling grand bazaar. Strategically move your merchant and assistants through the 16 locations, taking specific actions to increase your wheelbarrow capacity, fill it with goods, acquire special abilities, trade goods for rubies, and sell goods for money. The first player to collect five rubies wins, unless there is a tie, in which case money in hand is used to determine the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (160, 8, 'https://cf.geekdo-images.com/3-unuy5JW1cC8C6EUBNiOA__original/img/YJRIGmJmT0XuhRbOmLN8VdLfEec=/0x0/filters:format(jpeg)/pic4410877.jpg', true, 4, 1, 'That''s Pretty Clever!', 244522, 30, 'In Ganz schön clever, strategically choose your dice to place them in matching colored areas on your score sheet, creating chain-scoring opportunities and earning points. The dice you don''t use can still be utilized by other players, ensuring everyone remains engaged throughout the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (161, 10, 'https://cf.geekdo-images.com/g4XmxyKhNVdhC3QPd1purQ__original/img/pb6XFQZFUNOr6OPysOHB3usVDFk=/0x0/filters:format(jpeg)/pic3761012.jpg', true, 100, 1, 'Welcome To...', 233867, 25, 'In Welcome To..., you take on the role of an architect in 1950s United States, aiming to create the best new town by adding resources, hiring employees, and more. The game combines elements of a roll-and-write dice game, but without dice, instead using cards to create different action sets. Players mark their results on a score-sheet, filling in houses on their street in numerical order and taking actions to increase the point value of their estates or score points by building parks and pools. With simultaneous gameplay and random action sets, every game offers unique strategies and experiences.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (162, 12, 'https://cf.geekdo-images.com/6uEBiPxMH8gfGJ6f3HLNGg__original/img/p-g0LziOt6tE90cVJUvLZF0exfI=/0x0/filters:format(png)/pic1807124.png', true, 5, 3, 'Xia: Legends of a Drift System', 82222, 180, 'Xia: Legends of a Drift System is a 3-5 player competitive space adventure game. Each player starts as a hopeful captain of a small starship, flying around completing missions, exploring new sectors, and battling other ships. With customizable ships and the freedom to choose your own path, players compete to become the most famous captain in a constantly changing sandbox universe.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (163, 14, 'https://cf.geekdo-images.com/F58jczp5xk9CYFimrytXaw__original/img/m0S3T9_YWM7VvTW1c3pl4tBiu2c=/0x0/filters:format(jpeg)/pic2801874.jpg', true, 4, 2, 'Tyrants of the Underdark', 189932, 60, 'Tyrants of the Underdark is a territory control game where players lead a house of Drow in the Underdark. Players use their deck of cards to recruit minions, control sites, and assassinate enemy troops. The game combines deck-building with strategic gameplay, and the player with the most points at the end wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (164, 14, 'https://cf.geekdo-images.com/iCd6hMGDzsQ_UXh00iKsJA__original/img/QhbQ2AMud-_H0fliiSIvc0dowOU=/0x0/filters:format(jpeg)/pic4602347.jpg', true, 2, 2, 'Undaunted: Normandy', 268864, 60, 'Undaunted: Normandy is a deck-building game set during the D-Day landings in June 1944. Players take command of American or German forces and face intense resistance as they push deeper into France. Use strategic card play to seize the initiative, bolster your troops, and turn the tide of battle, while casualties remove cards from your deck.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (165, 14, 'https://cf.geekdo-images.com/BryJNSSDbCohXeaJPx3tpw__original/img/pgmg83e1lUqNo7FTLdscWNDwlPE=/0x0/filters:format(png)/pic4232048.png', true, 4, 2, 'War Chest', 249259, 30, 'War Chest is a bag-building war game where players draft units to form their armies and capture key points on the board. Each round, players draw three unit coins from their bag and use them to perform actions. The game ends when a player or team has placed all of their control markers on the board.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (166, 12, 'https://cf.geekdo-images.com/_LTujSe_o16nvjDC-J0seA__original/img/zUnAdsgt4dCxvtoRDc_9DHbic0M=/0x0/filters:format(jpeg)/pic5100947.jpg', true, 2, 2, 'Jaipur', 54043, 30, 'In the fast-paced card game Jaipur, you compete against another trader to become the most successful merchant in Jaipur and gain an invitation to the Maharaja''s court. Buy, exchange, and sell goods at the best prices, while managing your camel herds. Earn chips for selling goods, with higher rewards for selling multiples, but remember to use your camels strategically, as they are essential for trading and can contribute to your victory.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (167, 13, 'https://cf.geekdo-images.com/yTe9522sHL-3nhfAMH2cTg__original/img/zN6-x2bWP6lsDAC2FNsvU6ivQIc=/0x0/filters:format(jpeg)/pic1318481.jpg', true, 4, 3, 'Chaos in the Old World', 43111, 120, 'Chaos in the Old World is a game where you play as a god with distinctive powers and followers. You can corrupt and enslave the Old World using your diabolical abilities. The goal is to dominate the world, battling against rival gods and the inhabitants of the Old World.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (168, 12, 'https://cf.geekdo-images.com/JmdqlBJBynZMOciZzkVIkw__original/img/9LYzTUmOTO2enhJvqauMt6odC5Y=/0x0/filters:format(jpeg)/pic725882.jpg', true, 4, 2, 'War of the Ring', 9609, 120, 'War of the Ring is a board game where one player controls the Free Peoples and the other player controls Shadow Armies. The goal is to either win through military conquest or by successfully guiding the Ringbearer to Mount Doom while facing dangers and making strategic decisions. The game revolves around the roll of action dice, which determine possible actions and can also be used to play Event Cards that can alter gameplay.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (169, 14, 'https://cf.geekdo-images.com/YpQUtpxNtDHBF5eVcwzBtQ__original/img/br-kw0KuyBfH1mrxZ0kgVrF9UMc=/0x0/filters:format(png)/pic6478338.png', true, 2, 2, 'Radlands', 329082, 40, 'Radlands is a competitive dueling card game where you lead a post-apocalyptic group of survivors in a fight to protect your camps from a rival tribe. The main resource in the game is water, which is used to play people, events, and activate card abilities. Both players draw from the same deck and must strategically manage their cards and water to win.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (170, 14, 'https://cf.geekdo-images.com/ZN2rpiJ19lg5DZk_iYMMkQ__original/img/E7OjZXGfe2Vfj2qis30aXetbQJQ=/0x0/filters:format(jpeg)/pic1180640.jpg', true, 5, 1, 'Descent: Journeys in the Dark (Second Edition)', 104162, 120, 'In Descent: Journeys in the Dark (Second Edition), players can take on the role of the overlord or brave heroes. The game involves quests and exploration in dangerous environments as players battle monsters and try to stop the overlord''s evil plans. The game features a unique dice-based combat system and offers a story-driven campaign with modular board pieces and various character combinations.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (171, 14, 'https://cf.geekdo-images.com/4dmyKfqQm_uxA1-chh4JeQ__original/img/5UxDHPldIiCb0uovIwzngR-jx5M=/0x0/filters:format(jpeg)/pic3187001.jpg', true, 4, 2, 'Dominion (Second Edition)', 209418, 30, 'In Dominion, players strive to expand their small kingdom by acquiring land and resources. Each player starts with a small deck of cards and can purchase additional cards from a central selection. By strategically building their decks, players aim to accumulate victory points and become the most successful monarch. Dominion (Second Edition) introduces new kingdom cards to enhance gameplay, and the Dominion: Update Pack allows owners of the first edition to obtain these new cards without buying the entire game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (172, 18, 'https://cf.geekdo-images.com/gQOQW7p1RWHzY54Lrs-_pA__original/img/PSC8BO2njNcBJhxvi0IV5Jb5hgo=/0x0/filters:format(jpeg)/pic3315915.jpg', true, 6, 1, 'This War of Mine: The Board Game', 188920, 120, 'This War Of Mine: The Board Game is a tabletop adaptation of the award-winning video game that portrays the hardships and drama of civilians trapped in a war-torn city. Players take on the role of a group of trapped civilians, facing daily challenges such as finding shelter, searching for supplies, and defending against bandits. With a focus on human interactions and survival instinct, the game offers a multiplayer experience for up to 6 players and a solo variant, allowing players to make difficult decisions and face the consequences in their quest for survival.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (173, 8, 'https://cf.geekdo-images.com/843kZ6WR0HfyXWEybA6L7A__original/img/VtYGvhy_YCSw14FpV8FGzv0eMe4=/0x0/filters:format(jpeg)/pic4930887.jpg', true, 4, 2, 'Azul: Summer Pavilion', 287954, 45, 'In Azul: Summer Pavilion, players take on the role of master artisans commissioned to construct a summer pavilion for the Portuguese royal family. Over the course of six rounds, players strategically draft and place tiles on their individual player boards to score points. By completing stars and surrounding key elements on their boards, players can earn bonus tiles and maximize their chances of winning.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (174, 10, 'https://cf.geekdo-images.com/V1PyFDPNFY4bJFgreLPxmQ__original/img/NelFMJToi6WYyDQheBZiwCP7-qE=/0x0/filters:format(jpeg)/pic6929347.jpg', true, 2, 2, 'Splendor Duel', 364073, 30, 'In Splendor Duel, players compete against a rival guild to race for victory. Acquire Gem and Pearl tokens, purchase cards, gather bonuses, and gain prestige to outwit your opponent. This two-player standalone game offers new strategic opportunities and card powers, with shared gameplay mechanisms, dynamic interaction, and three victory conditions.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (175, 8, 'https://cf.geekdo-images.com/2AjxTtgBtZVst8wOAk1NAA__original/img/Ro6pGYz0WFmCOP5ytZ6Z40KhoZ4=/0x0/filters:format(jpeg)/pic6974498.jpg', true, 8, 2, 'Memoir ''44', 10630, 60, 'Memoir ''44 is a historical board game that allows players to recreate famous World War II battles. With over 15 different scenarios and a double-sided hex game board, players can experience the terrain, troop placements, and objectives of each army. The game is designed to be strategic yet not overly complex, and can be played with 2 players or in team play with up to 8 players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (176, 9, 'https://cf.geekdo-images.com/iyIO6udRfIn0xM1rIxyO2g__original/img/PZBFIDjPGZddto7n5F5gP_2slUA=/0x0/filters:format(jpeg)/pic5056685.jpg', true, 4, 2, 'Unmatched: Cobble & Fog', 294484, 40, 'Unmatched: Cobble & Fog is a highly asymmetrical miniature fighting game that features four new heroes. Players can choose to play as the Invisible Man, Sherlock with Watson, Dracula with the sisters, or Jekyll with Hyde. Each hero has a unique deck and players must strategically move and resolve combat to win. New heroes are regularly added to the game, providing fresh matchups for players to explore.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (177, 10, 'https://cf.geekdo-images.com/SoClIcwjDo_iZs0idVikDw__original/img/TEprFkOM3G_u2HnUvh05WITeXbg=/0x0/filters:format(jpeg)/pic4848330.jpg', true, 5, 1, 'Horrified', 282524, 60, 'In Horrified, you''ll confront seven terrifying monsters in a wretched town. Work with others to defeat these creatures before it''s too late. This game features high-quality miniatures of iconic monsters and offers cooperative gameplay with varying levels of difficulty, requiring strategic thinking to overcome each unique foe.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (178, 12, 'https://cf.geekdo-images.com/S8cE-Ld7XP5sVz-upKJ-Bg__original/img/c4ZrfOtpp6tDHLyMsKecc2ne370=/0x0/filters:format(jpeg)/pic1521633.jpg', true, 5, 3, 'Cosmic Encounter', 39463, 120, 'In Cosmic Encounter, players take on the roles of leaders of alien races competing for control of the universe. With unique powers and alliances, players engage in battles and strategic encounters to establish colonies in other players'' planetary systems. The objective is to be the first to have five colonies outside of their own system, using a combination of force, cunning, and diplomacy. Multiple players can achieve victory together through alliances.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (179, 10, 'https://cf.geekdo-images.com/Sz0wLg3TgycpzSvSMdzRJA__original/img/vi7OckGr7HWbTGOkEyxgcCUIfiU=/0x0/filters:format(jpeg)/pic3869603.jpg', true, 6, 2, 'Railways of the World', 17133, 120, 'Railways of the World (2009) is a strategic board game where players build their own railroad network, starting with a locomotive and a mission card. The goal is to connect cities, earn money, and expand your empire by developing faster locomotives and potentially building the Transcontinental Railway. The game includes multiple expansions and is based on the popular Railways of the World series.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (180, 13, 'https://cf.geekdo-images.com/LPa6rsGcv8S0-OeNjCOAEQ__original/img/AF8HHaIad1re-eg3kd9mlm2j_ss=/0x0/filters:format(jpeg)/pic1398895.jpg', true, 10, 5, 'The Resistance: Avalon', 128882, 30, 'In The Resistance: Avalon, players take on the roles of Good and Evil forces vying for control of Britain''s future. Mordred''s minions, disguised as Arthur''s warriors, are among the ranks of Good. Only Merlin knows who the agents of evil are, but he can only speak in riddles to reveal their identities. This standalone game can be played separately or combined with The Resistance.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (181, 14, 'https://cf.geekdo-images.com/dezQ4YjF03lZVxTdI-UJYw__original/img/smV4u2r1moRbzsSTzykECb06Mpo=/0x0/filters:format(jpeg)/pic6863204.jpg', true, 4, 1, 'Oathsworn: Into the Deepwood', 251661, 90, 'Oathsworn is a narrative-based game for 1-4 players set in the Deepwood. Players control the Oathsworn, a Free company fighting against unnatural horrors. The game features rich miniature combat encounters, multi-path storytelling, scripted boss fights, and a unique push-your-luck combat system. As the campaign progresses, players gain levels, loot, and new allies in their journey.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (182, 12, 'https://cf.geekdo-images.com/NEbmFFGhKwegIryUArgw6w__original/img/YCDNmHN9DiyAnjSvFRen1sjAug4=/0x0/filters:format(jpeg)/pic7110034.jpg', true, 5, 1, 'Nemesis: Lockdown', 310100, 180, 'Nemesis Lockdown is a stand-alone expansion to the original game, taking players to a secret base on Mars. With a multi-level board, players will explore rooms, gather items, and engage in intense gameplay, while completing secret objectives for victory. The expansion introduces new mechanics such as a multi-level base, advanced computer actions, and new characters and alien race.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (183, 13, 'https://cf.geekdo-images.com/jVAMlNeDMVZakxJPy-lDrQ__original/img/J4J6wVMItmWj4Yxt8JmuO1g76PE=/0x0/filters:format(jpeg)/pic7229993.jpg', true, 4, 1, 'Castles of Mad King Ludwig', 155426, 90, 'In the tile-laying game Castles of Mad King Ludwig, players compete to build the grandest castle for King Ludwig II of Bavaria. Players act as building contractors, adding rooms to their castles and selling services to other players. The game features a rotating Master Builder role, bonus rewards for completing rooms, and various methods of scoring points to determine the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (184, 14, 'https://cf.geekdo-images.com/PZt3EAAGV3dFIVuwMR0AEw__original/img/5ug9EeKlH_ucJEUXaFpTZbBJ1GY=/0x0/filters:format(jpeg)/pic3525224.jpg', true, 4, 1, 'Sagrada', 199561, 45, 'In Sagrada, players draft and strategically place dice to construct a beautiful stained glass window. Each player has a board with restrictions on color and value placement. Skill tokens can be used to break the rules, but at a cost. The player with the highest scoring window wins the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (185, 12, 'https://cf.geekdo-images.com/AGN1PRSFmhdUyoTjd-qw8A__original/img/Pl7doTXv2kAvdYGqkmvUYomkpJI=/0x0/filters:format(jpeg)/pic3752850.jpg', true, 2, 1, 'Too Many Bones: Undertow', 235802, 180, 'Too Many Bones: Undertow is a standalone expansion/sequel to the game, allowing players to join new playable characters Stanza and Duster as they journey down the Sibron River in search of answers. With the option to bring in additional Gearlocs from the original game or add in characters like Ghillie, Nugget, and Tink, this expansion offers an adventure for up to four players. Undertow introduces new baddies, encounters, tyrants, and a double-sided battle mat, with battles taking place both on land and on your raft for added variety and strategic gameplay.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (186, 14, 'https://cf.geekdo-images.com/eMJAona-uXwh6t2AcugGEg__original/img/4LuFgxwxB8OcjoGBWg6_IxFkZ0s=/0x0/filters:format(jpeg)/pic1063508.jpg', true, 2, 2, 'Sekigahara: The Unification of Japan', 25021, 180, 'The battle of Sekigahara in 1600 unified Japan under the Tokugawa family. Sekigahara is a 3-hour block game that allows players to re-contest the war as either Ishida Mitsunari or Tokugawa Ieyasu. With no dice used, combat is decided with cards and battles resolve quickly, but with suspense.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (198, 14, 'https://cf.geekdo-images.com/Dpu5nFJu4UUJ4Y50WyKueg__original/img/feQTYcCwgIg4LneCYIoaWz12Z24=/0x0/filters:format(jpeg)/pic7069368.jpg', true, 8, 2, 'Captain Sonar', 171131, 60, 'In Captain Sonar, you and your teammates control a submarine and must locate and destroy an enemy submarine before they do the same. Each player has a specific role, such as the captain or radio operator, and communication and organization are crucial. The game can be played turn-by-turn or simultaneously, and includes multiple maps with different difficulty levels.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (187, 14, 'https://cf.geekdo-images.com/TUtzY-F7gKTIKm9y8e1AQw__original/img/vpBmnZOOyLZA4va7N6S8j9NyGPQ=/0x0/filters:format(jpeg)/pic6520382.jpg', true, 1, 1, 'Final Girl', 277659, 60, 'Final Girl is a solitaire-only game where players take on the role of a female protagonist, fighting to survive by defeating a slasher. The Core Box provides all the necessary components, and additional Feature Film Boxes offer unique killers and locations for increased gameplay variety. Compared to Hostage Negotiator, Final Girl incorporates a game board to track movement, and allows for multiple character and killer choices. Please note that a 13 card Correction Pack was released for the first printing, addressing typos but not required to play.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (188, 13, 'https://cf.geekdo-images.com/MLvyN4VJ3QNXBKxli8XLiA__original/img/eDqWAa7vUdsYE6_LXVNzIdClbhw=/0x0/filters:format(jpeg)/pic5089618.jpg', true, 4, 1, 'Ora et Labora', 70149, 180, 'In Ora et Labora, players manage a monastery in the Medieval era, acquiring land and constructing buildings to gain resources and profit. The goal is to build an infrastructure and manufacture items for victory points. The game features mechanisms similar to Le Havre, and players have personal game boards, workers, and the ability to buy additional terrain. Two variants, France and Ireland, are included in the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (189, 14, 'https://cf.geekdo-images.com/2fgPg6Be--w97zoycObUgg__original/img/7PyNxWs85_OKGOm_jYVFvPwAViE=/0x0/filters:format(jpeg)/pic4815198.jpg', true, 6, 2, 'Dune', 283355, 180, 'Control the forces of a noble family, guild, or religious order on a barren planet, the only source of the universe''s most valuable substance. Recreate Frank Herbert''s incredible world in this acclaimed 40-year-old board game, where you''ll become the leader of one of six factions, all vying for control over the mysterious spice known as melange. Without it, space travel would be impossible. Choose your character and their forces, and decide who will control DUNE.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (190, 14, 'https://cf.geekdo-images.com/M_7UvwZvuxBVjxdadsa5AA__original/img/3tv6H7jX5T9rqwuI84YZKZDBGcw=/0x0/filters:format(jpeg)/pic1077906.jpg', true, 6, 3, 'A Game of Thrones: The Board Game (Second Edition)', 103343, 240, 'In the second edition of A Game of Thrones: The Board Game, three to six players take on the roles of the great Houses of Westeros, vying for control of the Iron Throne through diplomacy and warfare. Based on the best-selling A Song of Ice and Fire series by George R.R. Martin, this epic board game requires strategic planning, diplomacy, and clever card play to spread influence over Westeros. With updated graphics and additional elements from expansions, this edition introduces new innovations like player screens and Tides of Battle cards to bring unpredictability and intensity to combat.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (191, 8, 'https://cf.geekdo-images.com/bCdjNIu-O_SzVCfMLZY_KQ__original/img/GhYRV-yJmYEiTaZsZcnEA04YucA=/0x0/filters:format(png)/pic7634498.png', true, 3, 2, 'Ticket to Ride: Nordic Countries', 31627, 60, 'Ticket to Ride: Nordic Countries is a Nordic adventure game where players travel through Denmark, Finland, Norway, and Sweden to reach cities like Copenhagen, Oslo, Helsinki, and Stockholm. The goal of the game is to collect and play cards to place trains on the board and connect cities on ticket cards. This version is designed for 2-3 players, with a heavier focus on blocking opponents and aggressive play. Note that the Ticket to Ride Map Collection: Volume 5 - United Kingdom & Pennsylvania can be used as an expansion for Nordic Countries, but limited to three players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (192, 10, 'https://cf.geekdo-images.com/-PuiCnbxlBzMSjnUegp9AQ__original/img/hH1AwTR4fdtYY_cO8IYp1-I8dhE=/0x0/filters:format(jpeg)/pic5556025.jpg', true, 4, 1, 'The Red Cathedral', 227224, 80, 'Autumn in Moscow means it''s time for Tsar Ivan''s construction teams to prove they''re the best builders in the city. In The Red Cathedral, players take on the roles of these teams, competing to construct St. Basil''s cathedral and gain favor with the Tsar. This strategic Euro board game features various actions to assign sections, send resources, and gather more resources, requiring careful attention to opponents'' moves. Act quickly to complete sections, earn rewards, and gain recognition from the Tsar before the game ends.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (193, 13, 'https://cf.geekdo-images.com/g4mV4BH-ZrhMUVgil-yV1A__original/img/NjrnSg1aeia8I4eETDU3I1Br7a8=/0x0/filters:format(jpeg)/pic3016500.jpg', true, 5, 2, 'Dead of Winter: A Crossroads Game', 150376, 120, '"Crossroads" is a game series where players work together to survive in a post-apocalyptic world filled with monsters. In Dead of Winter, players lead different factions of survivors and must complete both the main objective and their personal secret objective. The game involves making difficult decisions that can impact the entire group, requiring players to balance their own interests with the well-being of the colony.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (194, 8, 'https://cf.geekdo-images.com/1YcHesB7LRvCF8i9vgH7qw__original/img/BVcGhHh0nedwcBBzu_ku-yh8LWY=/0x0/filters:format(png)/pic6009608.png', true, 4, 1, 'Suburbia', 123260, 90, 'In Suburbia, players build and develop a small town into a thriving city. Using hex-shaped building tiles, players add residential, commercial, civic, and industrial areas while also taking advantage of nearby towns'' resources. The goal is to have the highest population and strategically increase income and reputation to build valuable buildings and win the game. The second edition features updated artwork, larger tiles, a dual-sided scoreboard, and GameTrayz storage organizers.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (195, 12, 'https://cf.geekdo-images.com/qcASDsV-0eNBVipfPhxs7A__original/img/tyaZlnMJNbjwjRVcrbXpJBjrLRw=/0x0/filters:format(jpeg)/pic132447.jpg', true, 2, 2, 'Commands & Colors: Ancients', 14105, 60, '"Commands & Colors: Ancients" is a historical wargame that allows players to engage in stylized battles spanning from 3000 BC to 400 AD. The game features 15 battles, focusing on important terrain features and historically accurate deployment of forces. With a command card system, battle dice mechanics, and strategic gameplay, players must employ historical tactics and flexible battle plans to achieve victory in under an hour.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (196, 14, 'https://cf.geekdo-images.com/RjwAj6hw5aEMGvosEA12gw__original/img/jQUkqrDd0lOX6IRGq5Cbj1junL4=/0x0/filters:format(jpeg)/pic5879810.jpg', true, 4, 1, 'Star Wars: Outer Rim', 271896, 180, 'Embark on a thrilling journey in Star Wars: Outer Rim, a game where players take on the role of underworld characters seeking fame and fortune. Traverse the outer rim, hire iconic Star Wars characters, and compete to become the most renowned outlaw in the galaxy. Complete dangerous missions, upgrade your ship, and accumulate fame from various sources to achieve victory.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (197, 14, 'https://cf.geekdo-images.com/MHhQxXVjiaa6C-04nGiIWw__original/img/QyWen0FVOKD_4wf7Na7fdsMTjis=/0x0/filters:format(jpeg)/pic6640638.jpg', true, 5, 2, 'Space Base', 242302, 60, 'In Space Base, players take on the role of Commodores commanding a fleet of ships. Choose your strategy, whether it''s engaging in trade and commerce, building an income through mining, or spreading your influence with carriers. As you establish new colonies and gain influence, you can strive to be promoted to Admiral. This quick-to-learn, strategic dice game combines engine building with interesting card implications and offers a satisfying balance of luck and strategic choices.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (199, 13, 'https://cf.geekdo-images.com/0xqF_KyOb7V26Lu5YT3fxw__original/img/uqxMcj1QPt-U34drYdL6mmv2eos=/0x0/filters:format(jpeg)/pic6699821.jpg', true, 5, 1, 'Earth', 350184, 90, 'Earth is an open world engine builder game for 1 to 5 players. Tap into the rich environments of our planet to create natural synergies and expand your resources. With simple rules but endless strategic possibilities, each game uncovers new synergies and connections, mimicking the vastness of our real world.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (200, 12, 'https://cf.geekdo-images.com/5FBIAvZi3Sw8dvmfwwJTjg__original/img/HZYRVZoUtl7COYnS0NvXNSuaf80=/0x0/filters:format(jpeg)/pic2760568.jpg', true, 4, 2, 'Village', 104006, 90, 'Life in the village is challenging but allows for personal growth and success. Players control a family and must strive for fame and glory through various means, such as pursuing a career or exploring distant lands. Time is a crucial factor, as it continues to pass, and death is a natural part of life in the village. The game features a unique action mechanism that keeps turns short and tactically rich, with players strategically taking cubes from different zones on the board. The game ends when either the village chronicle or the anonymous graveyard is full.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (201, 8, 'https://cf.geekdo-images.com/6qAiZwWxSSKP2kFcA80cZg__original/img/oClfNpLGqRwyqeq54Na_VoVAA3o=/0x0/filters:format(jpeg)/pic1728832.jpg', true, 2, 2, 'Go', 188, 180, 'Go is a two-player game where players take turns laying stones on a grid. The objective is to claim territory by walling off sections of the board and surrounding each other''s stones. The game has a long history, dating back to ancient times, and is known for its depth and strategic gameplay. Additionally, a Go set can be used to play other abstract strategy games.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (202, 10, 'https://cf.geekdo-images.com/9r3qdPijtgoIN24PxFU2gw__original/img/tHQTKwwk4N8J5fpWhsoE8t0FQhw=/0x0/filters:format(jpeg)/pic5894745.jpg', true, 4, 1, 'Meadow', 314491, 90, 'Meadow is a set collection game where players compete to become the most skilled nature observer. With over 200 unique cards featuring hand-painted watercolor illustrations, players collect species, landscapes, and discoveries to earn points. By strategically placing path tokens on two boards, players can gain cards and activate special actions while working towards achieving goals. The player with the most points on cards and the bonfire board wins. Meadow also includes envelopes with additional cards to open at specific moments.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (203, 10, 'https://cf.geekdo-images.com/85t1wkwgvh3d2mmjsRcDrw__original/img/ejYTb1MVpf9optjLWlwbYua0TMk=/0x0/filters:format(jpeg)/pic6039256.jpg', true, 4, 1, 'Paleo', 300531, 60, 'Paleo is a co-operative adventure game set in the stone age where players must keep their human beings alive while completing missions. The ultimate goal is to paint a woolly mammoth on the wall, leaving a record of their existence for future generations. Players must navigate various challenges and acquire resources to survive and fulfill quests.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (204, 10, 'https://cf.geekdo-images.com/h1dRxq4lNMpuSUSGdgCmfA__original/img/MSX7waTxS81SiyxAmmCJFFWAl1I=/0x0/filters:format(jpeg)/pic7107431.jpg', true, 2, 1, 'Wingspan Asia', 366161, 70, 'Wingspan Asia is the third expansion to the popular game Wingspan, focusing on the unique and diverse bird species of Asia. It can be played as a standalone game for 1 or 2 players, or combined with other bird cards from the original game or other expansions. Additionally, it introduces a new Flock mode for 6-7 players, requiring components from the core game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (205, 10, 'https://cf.geekdo-images.com/rwOMxx4q5yuElIvo-1-OFw__original/img/Y2tUGY2nPTGd_epJYKQXPkQD8AM=/0x0/filters:format(jpeg)/pic1904079.jpg', true, 4, 2, 'Splendor', 148228, 30, 'Splendor is a Renaissance-themed game where players collect chips and develop cards to gain prestige points. Players can collect chips, buy and build cards, or reserve cards to prevent opponents from obtaining them. The goal is to reach a total of 15 prestige points before your opponents.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (206, 10, 'https://cf.geekdo-images.com/gz8_8iYP2SSGVAxpp7CwLg__original/img/9ULfNvkyu0k1m0d63-Mk0k5e0MA=/0x0/filters:format(png)/pic5854968.png', true, 4, 4, 'Tichu', 215, 60, 'Tichu is a partnership climbing card game where the goal is to get rid of your hand while earning points. The standard 52-card deck is used with four special cards added. Players take turns either playing a higher combination of cards or passing, and the last player out gives their cards to the first player who exited. Points are earned through bonuses such as Tichu and Grand Tichu, and the first team to reach 1,000 points wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (207, 14, 'https://cf.geekdo-images.com/JJFHx0RiUBYdy9bHAgVSuA__original/img/zwcZQov_aUfBN0z9J9PzsJqomeI=/0x0/filters:format(jpeg)/pic3956079.jpg', true, 4, 2, 'Coimbra', 245638, 90, 'In the 15th and 16th century, Portugal is thriving during the Age of Discovery. As the head of one of Coimbra''s oldest houses, your goal is to earn prestige by deepening relationships with monasteries and funding expeditions. Coimbra features an innovative dice mechanism and offers multiple paths to victory, ensuring that no two games are the same.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (208, 7, 'https://cf.geekdo-images.com/okM0dq_bEXnbyQTOvHfwRA__original/img/aVZEXAI-cUtuunNfPhjeHlS4fwQ=/0x0/filters:format(png)/pic6544250.png', true, 5, 2, 'Carcassonne', 822, 45, 'Carcassonne is a tile-placement game where players create a landscape by placing tiles depicting various elements like cities, roads, and cloisters. Players can also place meeples on these areas to accumulate points. The game offers strategic choices such as deciding where to place tiles to hinder opponents'' progress or maximize personal scoring.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (209, 12, 'https://cf.geekdo-images.com/uKaL10FK-u9wfYyK6MQaNg__original/img/_UDsTX8nCwdsynpoVtzHJHc8h6c=/0x0/filters:format(jpeg)/pic992459.jpg', true, 2, 2, 'Combat Commander: Europe', 21050, 180, 'Combat Commander: Europe is a card-driven board game set in World War II where one player controls the Axis (Germany) and another controls the Allies (America or Russia). Players use fate cards to activate their units on the mapboard and strategically move them to attack enemy units and capture objectives. The game incorporates chaos and uncertainty through random events, and each phase of gameplay represents several seconds of real time.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (210, 8, 'https://cf.geekdo-images.com/hLJv1A1TwrMrLLIJhVpKVA__original/img/LXqkRZSyg3hRMJlUMm2m83gmi_s=/0x0/filters:format(png)/pic3429338.png', true, 4, 1, 'Dinosaur Island', 221194, 120, 'In Dinosaur Island, players collect DNA and research sequences to bring extinct dinosaurs back to life. Compete to build the most thrilling park, attract and keep visitors each season. Make strategic decisions like growing herbivores or creating a pack of Velociraptors to balance excitement and safety.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (211, 10, 'https://cf.geekdo-images.com/wLto-xaabHwKQe_Bc4iD1Q__original/img/s3Gai8XBTVfWFFlYpL4XF3kIlWU=/0x0/filters:format(png)/pic3458036.png', true, 5, 3, 'Modern Art', 118, 45, 'In this game, players buy and sell paintings to earn profits. Each player takes turns auctioning off a painting from their hand, hoping to sell it to another player for a profit. However, if they choose to buy the painting themselves, they must pay the bank. At the end of each round, the value of paintings is determined by the number of paintings of that type sold. The player with the most cash after four rounds becomes the winner.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (212, 12, 'https://cf.geekdo-images.com/ecO5eAd3RkctQ0-Tbqv37Q__original/img/eVUBGX1Hjp91YAlj3XJtRQ3y444=/0x0/filters:format(jpeg)/pic5428723.jpg', true, 1, 1, 'Under Falling Skies', 306735, 40, 'Aliens have invaded Earth, and it''s up to you to defend the planet. Play as the commander of an underground bunker and fight off enemy ships in a multi-mission campaign. Strategically place dice to take actions and balance the risk of attracting more enemy ships. Expand your base, conduct research, and save humanity before the mothership destroys everything.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (213, 10, 'https://cf.geekdo-images.com/eCdiHCB9OobumwaGl4dbOQ__original/img/U4DY7_OUE1XI1m1Fi0iubbf2sEI=/0x0/filters:format(png)/pic6204967.png', true, 6, 1, 'Welcome to the Moon', 339789, 30, 'Welcome to the Moon is a flip-and-write game where players embark on a space colonization campaign across eight adventure sheets. Each turn, players choose from three combinations of starship numbers and corresponding actions to fill spaces on their adventure sheet. The game offers a unique experience with different mechanisms on each adventure sheet, and choices made in campaign mode impact future adventures, ensuring each campaign is unique.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (214, 13, 'https://cf.geekdo-images.com/lm8OOgJupqd3Q-fZ3DsQhw__original/img/hTV2P_-UPntaLbQB3Dl17LYz42A=/0x0/filters:format(jpeg)/pic3605785.jpg', true, 4, 2, 'Near and Far', 195421, 120, 'Join up to three friends in Near and Far as you embark on a quest to find the Last Ruin, a legendary city rumored to hold a powerful artifact. Along the way, you''ll recruit adventurers, collect supplies, and face challenges like bandits and robots. With a book of encounters and unique maps for each chapter, every game session promises an engaging and unforgettable story.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (215, 14, 'https://cf.geekdo-images.com/-hn7gA50ci0n7j9u_-jHhA__original/img/1RkLWWHzepOqacKarc1176UofVs=/0x0/filters:format(png)/pic1591406.png', true, 5, 1, 'Nations', 126042, 200, 'Nations is an intense historical board game where players control nations from prehistoric times to the beginning of World War I. Players must balance competing with other nations, managing their resources, and striving for great achievements. The game involves strategic decision-making and competition for historical events and victory points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (216, 10, 'https://cf.geekdo-images.com/PX7t5xXDLI2XqxoTShLayg__original/img/KYzd3fBTCxrRxEvNvzabNbll7Wg=/0x0/filters:format(jpeg)/pic5039625.jpg', true, 5, 2, 'Nidavellir', 293014, 45, 'In Nidavellir, you must defend the Dwarf Kingdom from the dragon Fafnir. As an Elvaland, you must search taverns, hire skilled dwarves, recruit heroic allies, and create a powerful battalion to defeat your mortal enemy. Strategically bid on taverns each turn, recruit dwarves with unique abilities, and increase the value of your gold coins to gain an advantage over other players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (217, 10, 'https://cf.geekdo-images.com/6O6HqeMY6z1MPPKKVKp93g__original/img/T8BDrCwo9eklFz5wnThjDYRC4ZE=/0x0/filters:format(jpeg)/pic7714854.jpg', true, 4, 1, 'Calico', 283155, 45, 'Calico is a tile-laying game where players compete to sew cozy quilts and attract cuddly cats. Collect and place patches of different colors and patterns to create aesthetically pleasing combinations. Score points for buttons, cats, and completing your unique quilt pattern.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (218, 14, 'https://cf.geekdo-images.com/Ip4TygnwFxuR57Vky9ZeZA__original/img/MP1e5GdDAT-4QO734-n8_rUkMv8=/0x0/filters:format(jpeg)/pic4490515.jpg', true, 2, 2, 'Paths of Glory', 91, 480, 'Paths of Glory: The First World War is a fast-paced card-driven wargame that allows players to recreate the dramatic events of World War I. Players can step into the shoes of the monarchs and marshals of the Central Powers or the Entente Powers, experiencing the struggle to win their rightful place or put an end to German militarism. With innovative game systems and a focus on cardplay, this game offers a challenging and immersive experience that can be played in just one evening.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (219, 14, 'https://cf.geekdo-images.com/_al0scMG_pQfGVM31Scf1Q__original/img/h4jBTaOjznJgWELa6tTrfPqqSeA=/0x0/filters:format(jpeg)/pic6107853.jpg', true, 5, 2, 'Ankh: Gods of Egypt', 285967, 90, 'In Ankh: Gods of Egypt, players embody Egyptian gods trying to survive as society forgets their existence. Through building caravans, summoning monsters, and converting followers, compete to be the last god standing. The game features stunning illustrations and miniatures, allowing players to feel like true deities as they reshape the foundations of Egypt using strategic and non-random gameplay mechanics.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (220, 9, 'https://cf.geekdo-images.com/a9Dco_g83IUWxrpxsw8ibw__original/img/Ho4DXQbjk8ze91242SC_L14toxs=/0x0/filters:format(png)/pic2844830.png', true, 2, 2, 'YINSH', 7854, 60, 'In YINSH, players strategically move rings on a board, leaving behind markers that change color when jumped over. The goal is to form a row of five markers with the player''s own color facing up. The first player to remove three of their rings wins, but each ring removed means having fewer pieces to play with.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (221, 10, 'https://cf.geekdo-images.com/fz4aYmycg9baQlm_DzwyKA__original/img/TvWwknCLoPNxx3uIOhlCg-9F1mM=/0x0/filters:format(jpeg)/pic7443423.jpg', true, 4, 2, 'My City', 295486, 30, 'My City is a competitive legacy game where players develop their own city board across different ages. With 24 episodes, players start in the preindustrial era and progress to industrialization. Choices made during gameplay carry over to create a personalized experience, and a double-sided game board allows for repeatable play without the legacy elements.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (222, 8, 'https://cf.geekdo-images.com/ZWJg0dCdrWHxVnc0eFXK8w__original/img/LgzEsQlF3xkSEQLoorc8ntiYiIY=/0x0/filters:format(jpeg)/pic38668.jpg', true, 5, 2, 'Ticket to Ride', 9209, 60, 'Ticket to Ride is an elegantly simple board game with a short learning curve. Players collect train cards to claim routes in North America and earn points. The game features high-quality components and has become a popular &quot;gateway game&quot; for new players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (223, 13, 'https://cf.geekdo-images.com/6QbaJOS8acWkJ6gSYI0KaQ__original/img/KOGjizqhVHXlKWxXkuyxkHRCNnA=/0x0/filters:format(jpeg)/pic584779.jpg', true, 5, 2, 'Cyclades', 54998, 90, 'In this collaborative game by Bruno Cathala and Ludovic Maublanc, players compete to build two cities in the Ancient Greek island group called the Cyclades. To win, players must show respect to all five gods and cannot sacrifice to only one. Each turn, players bid for the favor of the gods, with each player limited to the favor of one god per turn.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (224, 13, 'https://cf.geekdo-images.com/bUbrvlY6Dw1cdb-sNrnkew__original/img/KcU-Srht_4xyLQ69ZjFcFKEOfMc=/0x0/filters:format(jpeg)/pic5188761.jpg', true, 5, 2, 'Wonderland''s War', 227935, 125, 'The Looking Glass has shattered, causing madness and war in Wonderland. In Wonderland''s War, 2-5 players compete to gather forces, build towers, recruit allies, and complete quests. After battling and strategizing for three rounds, the faction with the most points becomes the new leader of Wonderland.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (225, 11, 'https://cf.geekdo-images.com/0d1EaMVmAiIBROI2QstFSQ__original/img/udyeiYgfDrEymxhfgtqODm0GwwA=/0x0/filters:format(jpeg)/pic3596681.jpg', true, 2, 2, 'Codenames: Duet', 224037, 30, 'Codenames Duet is a cooperative version of the popular game Codenames where players work together to find all of their agents on a 5x5 grid of word cards. The goal is to reveal all fifteen agents without uncovering an assassin before time runs out. Players take turns giving one-word clues to their teammate, who will then try to identify the corresponding agent on the grid.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (226, 12, 'https://cf.geekdo-images.com/ufUK_7N_13rYhsIurbbtzg__original/img/eKzUqIgkc34WsSVq9zSW6sjZgbc=/0x0/filters:format(jpeg)/pic2031777.jpg', true, 4, 1, 'La Granja', 146886, 120, 'In La Granja, players compete to develop and control farms near the village of Esporles on the island of Mallorca. Over six rounds, players expand their farms, deliver goods to the village, and strive to earn the prestigious title of "La Granja." Strategic planning, observation of others, and adaptability to changing circumstances are key to victory in this engaging and thrilling game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (227, 10, 'https://cf.geekdo-images.com/3HkjDovk8Yr2wMumcSUGog__original/img/hl_WEzG4mKaEW_7qrZf-sPxXMpI=/0x0/filters:format(jpeg)/pic4843622.jpg', true, 6, 1, 'Planet Unknown', 258779, 80, 'In Planet Unknown, players compete to develop the best planet for colonization. Each player strategically places polyomino-shaped, dual-resource tiles on their planet, representing the infrastructure needed to support life. Tile placements can trigger meteors, making planet development harder. The game offers simultaneous, strategic turn-based play through the Lazy S.U.S.A.N. space station.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (228, 14, 'https://cf.geekdo-images.com/quoCefZvHWeaf31_8HvFSA__original/img/y8GWcMFHQSNI754bZlDORXpjLuw=/0x0/filters:format(jpeg)/pic3979852.jpg', true, 4, 1, 'Aeon''s End: Legacy', 241451, 90, 'In this game, you are a young apprentice learning to become a breach mage, a powerful protector of the last bastion known as Gravehold. Your mentors are legendary mages who have defended the city against the Nameless, a constant threat. Through overcoming ordeals and sacrificing your life for Gravehold, you will become a breach mage and join the ranks of these heroes. Aeon''s End: Legacy is a standalone game, not requiring the original Aeon''s End.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (229, 13, 'https://cf.geekdo-images.com/rAQ3hIXoH6xDcj41v9iqCg__original/img/7oKwNUYakx3-vHUBLHWVuFNKfl4=/0x0/filters:format(jpeg)/pic5164305.jpg', true, 10, 5, 'Secret Hitler', 188834, 45, 'Secret Hitler is a political intrigue game set in 1930s Germany. Players are randomly assigned as liberals or fascists, with one player designated as Secret Hitler. The fascists work together to install their leader while the liberals try to find and stop Secret Hitler. Each round, a President and Chancellor are elected to enact laws and players must navigate the advancing fascism to achieve their objectives.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (230, 14, 'https://cf.geekdo-images.com/V0OZ9QR0pC9G5t5i9MoZTQ__original/img/NxxAoxVxMNkDGEkD3aoobPL14dI=/0x0/filters:format(jpeg)/pic6950224.jpg', true, 4, 1, 'Revive', 332772, 120, 'Revive is a 1-4 player game where players must revive civilization and explore a frozen earth. Players lead their tribes, harness resources, recruit survivors, build factories, and relearn forgotten technologies. The game features asymmetric player powers, highly variable setup, and a 5-part campaign that unlocks additional content. Endless replayability is possible once all content is unlocked.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (231, 14, 'https://cf.geekdo-images.com/ZrRidumkzu62HuwKdgQpHA__original/img/FqPcmpzntFV1FDR6dniHGKvNwvI=/0x0/filters:format(jpeg)/pic1430769.jpg', true, 5, 1, 'Legendary: A Marvel Deck Building Game', 129437, 60, 'Legendary: A Marvel Deck Building Game is a strategic card game set in the Marvel Comics universe. Players choose a mastermind villain and hero decks, recruiting powerful heroes and building their fighting abilities to combat the villains. Players collect villain cards and fight the mastermind to earn points, while also preventing the mastermind from completing their scheme. The game ends when all attack cards are claimed or if the mastermind completes their scheme, resulting in a win or loss for the players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (232, 12, 'https://cf.geekdo-images.com/H-AU-nhjCbaxWlWj_O-eFw__original/img/lA3TNJgLhVo-hB9rPKxr66js5j0=/0x0/filters:format(jpeg)/pic4873141.jpg', true, 4, 2, 'The Taverns of Tiefenthal', 269207, 60, '"The Taverns of Tiefenthal" is a strategy game where players aim to attract new wealthy guests to their tavern. By skillfully choosing dice and developing their deck of cards, players must decide whether to focus on making money or keeping the beer flowing. With five modules to add complexity, this award-winning game offers a challenging and immersive experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (233, 14, 'https://cf.geekdo-images.com/1COY3oeK9aN2_XNimKaNww__original/img/ZzyzlO15ggCfkLg9ckeM4PWNePI=/0x0/filters:format(jpeg)/pic3033330.jpg', true, 12, 4, 'Deception: Murder in Hong Kong', 156129, 20, 'Deception: Murder in Hong Kong is a deduction and deception game for 4-12 players that takes about 20 minutes to play. Players take on various roles, including the Forensic Scientist, Investigators, Murderer, Accomplice, and Witness. The Investigators must deduce the truth from clues given by the Forensic Scientist while the Murderer and Accomplice try to mislead them. The game is a battle of wits to uncover the truth and identify the killers before they get away with murder.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (234, 12, 'https://cf.geekdo-images.com/1M-1d6fr3U9t670MCVOTeg__original/img/QxxRpFr57KrNdyFSzslHPN95mSA=/0x0/filters:format(jpeg)/pic5896816.jpg', true, 4, 1, 'MicroMacro: Crime City', 318977, 45, 'In MicroMacro: Crime City, players must solve 16 cases by closely examining a giant city map to uncover hidden information and trace the paths of the criminals. Each case consists of cards that prompt players to find specific locations on the map or uncover important information about the case. Players must piece together the events of each case using the map and their deduction skills. Can you successfully answer all the questions and solve the crimes?');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (235, 8, 'https://cf.geekdo-images.com/1bihoShOuqaCuZJXB3Q2gQ__original/img/fvGzpqyud6Oz1BWOe4gItWWb8P0=/0x0/filters:format(jpeg)/pic3936124.jpg', true, 5, 2, 'Century: Golem Edition', 232832, 45, 'Century: Golem Edition is a re-themed version of Century: Spice Road where players act as caravan leaders delivering crystals on the golem road. Players take turns to establish trade routes, make trades or harvest crystals, fulfill demands, or rest by taking back played cards. The game ends when a player claims their fifth victory point card, and the player with the most victory points wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (236, 10, 'https://cf.geekdo-images.com/uiVZ7QVfRFQx3EBG8eDU8w__original/img/tLBrF40IDpARzoiUor4wwUvWXgY=/0x0/filters:format(png)/pic6339446.png', true, 2, 2, 'Hanamikoji', 158600, 15, 'Welcome to Hanamikoji, the renowned Geisha street in the old capital, where two players compete to win the favor of seven illustrious Geishas by collecting their preferred performance items. Use careful strategy and bold moves to outsmart your opponent and earn the most Geishas'' favor. Jixia Academy offers the same gameplay as Hanamikoji but with unique artwork.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (237, 14, 'https://cf.geekdo-images.com/kVHuMBxmathfnq42XINBQg__original/img/Xak18xf2xfSedYFS9-ZKwjSk7yc=/0x0/filters:format(jpeg)/pic2482309.jpg', true, 6, 1, 'Zombicide: Black Plague', 176189, 180, 'Zombicide: Black Plague transports players into a medieval world infested with zombies. Take control of various fantasy characters like paladins and magicians, wielding weapons and casting spells to fight the undead and their Necromancer leaders. With revamped rules, intense gameplay, and a variety of missions, it''s up to your group of survivors to reclaim the realm and eradicate the zombie apocalypse.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (238, 10, 'https://cf.geekdo-images.com/enmQAOQl99U6wiQvZoL5GQ__original/img/iiKhufERu8v8JpErc5kUoO8WiNw=/0x0/filters:format(jpeg)/pic2556921.jpg', true, 4, 1, 'Roll Player', 169426, 90, 'In Roll Player, create the ultimate fantasy hero by customizing their race, class, alignment, skills, traits, and equipment. Roll and draft dice to improve attributes, purchase weapons and armor, and train to gain skills. Earn Reputation Stars by constructing the perfect character and compete to be the player with the greatest Reputation.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (239, 13, 'https://cf.geekdo-images.com/cCLn9Mvb7jRSaZzHeUXhoQ__original/img/NK4hsA9nc-kdI07hR0Nc23dL_bk=/0x0/filters:format(jpeg)/pic6937913.jpg', true, 4, 2, 'Clank!: Catacombs', 365717, 90, 'Delve into the treacherous catacombs of Umbrok Vessna, the skeletal dragon. Navigate portals, collect riches, and rescue prisoners while avoiding vengeful ghosts. In Clank! Catacombs, a deck-building adventure, you create unique dungeon layouts with tiles and can incorporate cards from previous Clank! expansions. Explore this dangerous world, find your fortune, and escape the wrath of the dragon.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (240, 12, 'https://cf.geekdo-images.com/rzSmS-GD8_pGl9bWo07FWQ__original/img/TFtpuGRO0cbVjWl03YhtzTBRk5A=/0x0/filters:format(jpeg)/pic5638431.jpg', true, 4, 2, 'Kanban: Driver''s Edition', 109276, 120, '"Kanban: Automotive Revolution" is a Eurogame focused on economics and resource management. Players take on the role of ambitious managers in an assembly line, working to impress the board of directors and achieve high positions in the company. Through solid management, players must manage suppliers and supplies, improve automobile parts, and make strategic decisions to stay ahead of their peers.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (241, 14, 'https://cf.geekdo-images.com/-A_ABjMw4PdoAZrH-FjiiA__original/img/GuDlOog3eTRrkxCUzCEgRc17B-g=/0x0/filters:format(png)/pic5726930.png', true, 4, 1, 'Darwin''s Journey', 322289, 120, 'In Darwin''s Journey, players embark on an adventure through the Galapagos islands, exploring its beauty and biodiversity. As workers, players must study various disciplines to perform actions like exploration and gathering, all while contributing to the advancement of human knowledge in biology. With short- and long-term objectives, every action taken earns victory points, making for an engaging and strategic gaming experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (242, 12, 'https://cf.geekdo-images.com/GdXWqyTfO8F_M9hFtfo-HA__original/img/832FztQEizwRR3FnHIx1Zgjkw8Y=/0x0/filters:format(jpeg)/pic7565506.jpg', true, 5, 2, 'The Princes of Florence', 555, 100, 'In The Princes of Florence, players compete to become the most prestigious family in Florence by attracting artists and scholars to their palace. Through seven rounds, players score points by playing profession cards to generate "work points" that can be exchanged for money or victory points. The game features a variety of professions, each attracted to specific combinations of building, landscape features, and social freedom, which players acquire through auctions. Meeting the increasing minimum requirement of work points each round is necessary to convert the points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (243, 8, 'https://cf.geekdo-images.com/2f9uTicUSXkdPp2Yks6zFw__original/img/WQcFmlhYBMIp8f6gNNVhnnue1w4=/0x0/filters:format(jpeg)/pic5885689.jpg', true, 8, 2, 'Sushi Go Party!', 192291, 20, 'Sushi Go Party! is an expansion of the popular game Sushi Go! that offers more options and allows for up to eight players. The game introduces new dishes and customization choices, such as mega maki, super sashimi, and endless edamame. Enjoy the sushi-feast with friends and earn points by creating winning sushi combos.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (244, 8, 'https://cf.geekdo-images.com/sUrK5CCscls-tTc2hJWDPg__original/img/hQOFypZgCn4CQrJnXiNaQc_hjCw=/0x0/filters:format(jpeg)/pic3482521.jpg', true, 4, 2, 'Flamme Rouge', 199478, 45, 'Flamme Rouge is a fast-paced, tactical bicycle racing game where players control a team of two riders. The goal is to be the first to cross the finish line with one of their riders. Use strategic card play and slipstreams to position your team for a well-timed sprint to victory.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (245, 16, 'https://cf.geekdo-images.com/wJphcoCCYScWCNkE32D_hw__original/img/qSYnPWdXgggfxYvT03Oa32zSrh0=/0x0/filters:format(jpeg)/pic5756205.jpg', true, 5, 1, 'Detective: A Modern Crime Board Game', 223321, 180, 'Detective: A Modern Crime Board Game is a immersive detective game where players solve five interconnected cases, using resources like a dedicated database and the internet to uncover clues. This 2020 Game of the Year edition includes a set of character portrait photos to enhance gameplay immersion. Work as a team or go solo in this modern setting detective game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (246, 9, 'https://cf.geekdo-images.com/4AlLbprNustr9G7pbkGRuw__original/img/d7Z89SEv9T_HqPaYEomeKcFmgeA=/0x0/filters:format(jpeg)/pic4621579.jpg', true, 4, 2, 'Unmatched: Battle of Legends, Volume One', 274637, 40, 'Unmatched is a highly asymmetrical miniature fighting game for two or four players, featuring unique heroes with their own decks. Tactical movement and strategic combat resolution create a rewarding play experience. Battle of Legends, Volume One includes four heroes with distinctive abilities, and the game includes a double-sided board, miniatures, and custom life trackers with stunning artwork.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (247, 14, 'https://cf.geekdo-images.com/A7IOpPt-lHrMYsbRmxXWdQ__original/img/kaYIVoFZkbWe0z80bvcqJdFCNlo=/0x0/filters:format(jpeg)/pic6177962.jpg', true, 6, 2, 'Fantasy Realms', 223040, 20, 'As ruler, build the mightiest realm by choosing military tactics or sorcery in Fantasy Realms, a card game. Draw and discard cards to create the best combos and aim for the highest score. Use the WizKids Games Companion app to track scores and lookup cards. Available on iOS and Google Play.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (248, 12, 'https://cf.geekdo-images.com/9sbSKcc-Fm8Od5wwuf3kgg__original/img/kNoFZrbwsvMvs_iKWeKBHYLka5Y=/0x0/filters:format(jpeg)/pic1215969.jpg', true, 4, 2, 'Goa: A New Expedition', 9216, 90, 'Goa is a strategy game set in the 16th century, where players compete as trading companies dealing in spices and resources. The game involves auctions, resource management, and making strategic investments to improve ships, plantations, and more. With a combination of interactive auctions and solo plantation management, players must make tough decisions to rise to the top.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (249, 8, 'https://cf.geekdo-images.com/665Ply7Ho1WVf1v1iZlWeg__original/img/4q6RCxd7st-yW2MyrJwVC_d3ajk=/0x0/filters:format(png)/pic3283110.png', true, 4, 2, 'Santorini', 194655, 20, 'Santorini is a strategy game that has been continuously developed and refined over the past 30 years. It is accessible for all ages and skill levels, with simple rules involving moving and building structures. The game also features 40 different god and hero powers that add depth and variety to the gameplay.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (250, 12, 'https://cf.geekdo-images.com/1BMyuEwK0zV9005oYinYLw__original/img/3OigysLof9HIk4jvV79Cm-tb-gY=/0x0/filters:format(jpeg)/pic4317519.jpg', true, 4, 1, 'Chronicles of Crime', 239188, 90, 'Chronicles of Crime is a cooperative crime investigation game that incorporates an app, a board game, and Virtual Reality. With the same physical components, players can solve numerous crime scenarios. By using unique QR codes on the components, the app activates different clues and stories, allowing for continuous updates and new scenarios. The game includes a VR experience that only requires a mobile phone.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (251, 8, 'https://cf.geekdo-images.com/GCxsnITFBO1pNXiBYkq_qA__original/img/8uoTbz4B_M1GbT4N3vGN-bhZF2Q=/0x0/filters:format(jpeg)/pic2524838.jpg', true, 5, 2, 'Isle of Skye: From Chieftain to King', 176494, 50, 'Isle of Skye is a tile-laying game where 2-5 players are chieftains of clans competing to build their kingdoms and score the most points. Each game is different thanks to scoring tiles, leading to different tactics and strategies. Players must also manage their money as they set selling prices for tiles, aiming to have the best kingdom at the end to become the sovereign of the island.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (252, 8, 'https://cf.geekdo-images.com/oPNuEm_dR987BKoYEF9zGQ__original/img/ZNqEkLMKkrIPxRduZWmWC6VBgoY=/0x0/filters:format(png)/pic7414566.png', true, 2, 2, 'KLASK', 165722, 10, 'The KLASK game board is shaped like a ball field with two goals on each end. Your gaming piece is a black magnet that you control with a large magnet under the board. The objective is to push the small red ball past the obstacles and your opponent into the goal hole. Score points by having the ball stay in your opponent''s hole, having the white magnetic pieces stick to your opponent''s gaming piece, or causing your opponent to accidentally pull their gaming piece into their own goal hole. The winner is the first player to reach the KLASK field.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (253, 12, 'https://cf.geekdo-images.com/S_-n-WRvpzgH-d8xuzJgmg__original/img/cvWVDcSUBsngibbELMhABgIyrVo=/0x0/filters:format(png)/pic1079512.png', true, 5, 2, 'Glory to Rome', 19857, 60, 'In 64 A.D., Emperor Nero Caesar returns to Rome to set up shelters for the displaced population after a great fire destroys much of the city. As a player reporting directly to Nero, your task is to rebuild structures and restore Glory to Rome. This card-based city building game features unique mechanics where each card can be used as a building, client, raw material, or resource, leading to strategic decision-making. Additionally, the game involves role selection and interactions between different card uses, with scoring based on completing buildings and storing resources.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (254, 14, 'https://cf.geekdo-images.com/6TOzhqw0BXNxbUZCl0k1sg__original/img/tj-4BHcZxjUmiJpMnPCxXF1g0YY=/0x0/filters:format(jpeg)/pic6285702.jpg', true, 4, 2, 'Pulsar 2849', 228341, 90, 'In the year 2849, players explore space, claim pulsars, and discover technologies to build energy-distribution infrastructure in the Euro-style game, Pulsar 2849. In 8 rounds, players roll dice, draft them to take actions such as flying survey ships, developing pulsars, building energy transmitters, patenting technologies, buying dice modifiers, and completing special projects in their headquarters. Points are scored each round based on discoveries and exploration, with common goals for all players to achieve.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (255, 12, 'https://cf.geekdo-images.com/BZiR636JJdSBwThtH42DVw__original/img/ntif6KjmWg9hGgDmT_4qEIIXv5s=/0x0/filters:format(jpeg)/pic3744052.jpg', true, 4, 2, 'Hero Realms', 198994, 20, 'Hero Realms is a fantasy deck-building game based on the popular Star Realms. Players start with a deck of gold and weapons, using gold to buy champion and action cards from the shared Market deck. The goal is to reduce your opponent''s health to zero by attacking their champions, with expansions available for additional character choices and cooperative or competitive gameplay options.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (256, 14, 'https://cf.geekdo-images.com/3EmD1SEI5fVpR4rbdkU0AA__original/img/M6qiL7ZNU_mjgZXHZjOrVxNamfU=/0x0/filters:format(jpeg)/pic882119.jpg', true, 7, 2, '1830: Railways & Robber Barons', 421, 360, '1830 is a renowned 18xx game known for its lack of chance elements, ensuring consistent outcomes. Players aim to generate wealth by trading stocks in various eastern United States companies on a hexagonal board. This edition, published in 2011 by Mayfair Games and Lookout Games, includes the original classic design, a faster basic game, and additional variants developed by renowned railroad game designers.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (257, 12, 'https://cf.geekdo-images.com/AdJBpY_o7PQEcoJPU1QdTg__original/img/6iXYfqlV5oZgqMAUHyWVKxn_7AM=/0x0/filters:format(jpeg)/pic5869977.jpg', true, 4, 1, 'Hallertau', 300322, 140, 'Set in 1850s Bavaria, Germany, this game immerses players in the Hallertau region, famous for its hops cultivation. As the chief of a small village, you aim to increase its wealth and reputation through agriculture and sheep breeding. The game features a progressive worker placement system, a two-field rotation system, strategic card play, and the challenge of managing the lifespan of your sheep.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (258, 12, 'https://cf.geekdo-images.com/j6UzppDoJv9tKHQl-pWpIw__original/img/Yu1D0BNyavkX_SV8EK056FxfRsw=/0x0/filters:format(jpeg)/pic3336315.jpg', true, 5, 3, 'Shogun', 20551, 120, 'In this game set in Japan during the Sengoku Period, players take on the role of Daimyos and lead their troops to conquer provinces. Each player has 10 actions to develop their kingdom and score points, with battles resolved using a unique Cube Tower. Owning provinces, temples, theaters, and castles earn points, and the player with the highest score becomes the Shogun. This international edition of the game is based on the Wallenstein system and includes language-independent components.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (259, 10, 'https://cf.geekdo-images.com/o9-sNXmFS_TLAb7ZlZ4dRA__original/img/TPKZgpNxB_C73RNbhKyP6UR76X0=/0x0/filters:format(jpeg)/pic3211873.jpg', true, 4, 2, 'Samurai', 3, 60, 'Samurai is a strategic game set in medieval Japan. Players compete for the favor of three factions: samurai, peasants, and priests, represented by different figures on the board. The game involves using hexagonal tiles to curry favor with these factions, and players must surround figures to gain them.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (260, 8, 'https://cf.geekdo-images.com/pRZrHJNoPQ3gay9EkpuZLw__original/img/VHOeHhfYK3n-wYSP0G9RT26U0uQ=/0x0/filters:format(jpeg)/pic4304741.jpg', true, 8, 3, 'Camel Up (Second Edition)', 260605, 45, 'Camel Up is a fun and exciting game where players bet on racing camels to determine the winners. The game incorporates dice rolling to determine how the camels move and race towards the finish line. With new artwork, game board design, and game modes, Camel Up guarantees an unpredictable and thrilling race every time.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (261, 10, 'https://cf.geekdo-images.com/gLFt1Kif5Cfag505_COYYw__original/img/5vUNCkVDoQpcTfmlhFzkS4e1EaQ=/0x0/filters:format(jpeg)/pic3926631.jpg', true, 4, 2, 'Galaxy Trucker', 31481, 60, 'In Galaxy Trucker, players take on the role of brave space truckers who must build and fly their own spaceships. During the building phase, players grab tiles to construct their ships within a time limit. They must strategically place various components, such as guns and engines, to create a durable and efficient ship. Then, during the flying phase, players navigate through various challenges and obstacles, earning credits by delivering goods and defeating pirates. The player with the most credits at the end of three rounds wins the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (262, 12, 'https://cf.geekdo-images.com/p-k_CqUvJzTcMMz4ms7f_Q__original/img/jCMiFqLiGxgXM7akNX3KLHEI6-o=/0x0/filters:format(jpeg)/pic3499166.jpg', true, 2, 2, '1960: The Making of the President', 27708, 120, '"1960: The Making of the President" is a fast-paced strategy game where players take on the roles of John F. Kennedy and Richard M. Nixon. Set during the Cold War era, players must navigate the challenges of foreign policy, social turmoil, and the economy as they compete for the presidency. With a card-driven game system and specific event cards, players must adapt their game plan to secure electoral votes and dominate the issues of the day to ultimately win the White House.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (263, 10, 'https://cf.geekdo-images.com/IB38fOp0mTvFuvfdHczf0Q__original/img/SN5DSgS5Z0GOEzTBulGgX3faHCA=/0x0/filters:format(jpeg)/pic392515.jpg', true, 5, 3, 'Steam', 27833, 90, 'Steam is a strategy game on Steam where players build railroads and deliver goods on a changing network of tracks and stations. The game features a double-sided game board depicting different regions for different player counts, and players must balance their investments and deliveries to earn income and victory points. Similar to Railways of the World, the game has modified mechanics and artwork.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (264, 12, 'https://cf.geekdo-images.com/XmwCsjQ3C1Ul53ZN2o8Q-Q__original/img/sUuboe9L4_nrIokyjAE74uklCjE=/0x0/filters:format(png)/pic2617634.png', true, 4, 2, 'T.I.M.E Stories', 146508, 90, 'T.I.M.E Stories is a narrative board game where players act as temporal agents sent to different worlds to complete missions and prevent temporal faults. Each player takes on a deep role in the story and uses Temporal Units (TU) to navigate and solve puzzles, aiming to make the perfect run. The game includes various scenarios, with each consisting of a deck of cards that players explore to uncover surprises, enemies, clues, and more.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (265, 13, 'https://cf.geekdo-images.com/WrnWFA1Sysm3-nQyBe1sUA__original/img/cGg9S0AH41VxczdybvFsb81AKWw=/0x0/filters:format(jpeg)/pic2619743.jpg', true, 6, 2, 'Viticulture', 128621, 90, 'In Viticulture, players manage small vineyards in rustic Tuscany, trying to turn them into successful wineries. They allocate workers throughout the year, completing tasks during different seasons. Visitors can be assigned to help out, and players can expand their vineyards and fill wine orders to achieve the goal of running the most successful winery in Tuscany.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (266, 14, 'https://cf.geekdo-images.com/8kEIlWY3rg27NgmVBZkFyg__original/img/LgCwqEKxO2mpOzXf5_6z_pt6vz0=/0x0/filters:format(jpeg)/pic4101638.jpg', true, 5, 2, 'Vindication', 224783, 150, 'Vindication is a highly strategic tabletop game for 2-5 players. Set in a fantasy world, players must manage resources, control areas, and select actions to add companions, acquire relics, and defeat monsters. With unique mechanics, over 72 card abilities, and variable tile placement, each game offers high replayability.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (267, 14, 'https://cf.geekdo-images.com/NMUqU-PXbEbdLUmzWUlKSw__original/img/Olwn4qVquS8rDnNKfVFH7BjKNt4=/0x0/filters:format(jpeg)/pic5594286.jpg', true, 4, 2, 'Clash of Cultures: Monumental Edition', 299659, 240, 'Clash of Cultures: Monumental Edition is a game of exploration, expansion, and development. Players lead civilizations from single settlements to mighty empires, exploring their surroundings, building cities, researching advances, and conquering rivals. With a modular board, distinct advances, seven wonders, and miniatures and cards, this edition includes fully-sculpted miniatures of the Seven Wonders for an immersive gaming experience.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (268, 14, 'https://cf.geekdo-images.com/lZwgk3_MqyAmROjkz4v8Wg__original/img/W0gWMrkx5fEgVp8b2fMc-LfGmz8=/0x0/filters:format(png)/pic6974323.png', true, 6, 2, 'Western Legends', 232405, 90, 'Western Legends is an open-world tabletop adventure set in the American Wild West for 2-6 players. Players take on the roles of historic figures and earn their legendary status by engaging in various activities such as gambling, cattle driving, gold prospecting, robbing banks, and more. The game features intuitive gameplay, beautiful artwork, and the winner is determined by earning the most Legendary Points (LPs).');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (269, 12, 'https://cf.geekdo-images.com/7kqDmkUMGxXHr1wNPA7Gvg__original/img/e6rS0PyrVlPpJjCsWPmCaGg9PXc=/0x0/filters:format(jpeg)/pic4884996.jpg', true, 5, 1, 'Tapestry', 286096, 120, 'Tapestry is a two-hour game for 1-5 players where you create a unique civilization with a storied history. Advance on four different tracks to gain benefits and victory points while building your capital city and leveraging your abilities. Earn tapestry cards to tell the story of your civilization.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (270, 12, 'https://cf.geekdo-images.com/TFX_awTwbJxoG74GtRHytw__original/img/plU9ZhUubz-1LHGfbsDstEOE9vg=/0x0/filters:format(jpeg)/pic5624523.jpg', true, 4, 1, 'Endless Winter: Paleoamericans', 305096, 120, 'Endless Winter: Paleoamericans is a euro-style game set in North America around 10,000 BCE. Players guide their tribes from nomadic hunter-gatherers to prosperous societies, migrating, settling new lands, and building megalithic structures. Combining worker placement and deck building, players send tribe members to action spaces and use cards to pay for actions and trigger bonus actions. The game features interconnected systems and multiple paths to victory, and after four rounds, the tribe with the highest score wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (271, 14, 'https://cf.geekdo-images.com/IiJ1JK6AjlSCKsC8U_DqMw__original/img/4W8ffc5xOvC6sl660XMGCBqkzJ8=/0x0/filters:format(jpeg)/pic5375937.jpg', true, 5, 1, 'Lewis & Clark: The Expedition', 140620, 120, 'Lewis & Clark is a board game where players take on the role of explorers trying to be the first to reach the Pacific coast. They must cleverly manage their characters and resources as they navigate unknown territory, utilizing dual-use cards to make decisions. Timing and using other players'' positions strategically are key to achieving victory in this historically themed game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (272, 10, 'https://cf.geekdo-images.com/Naw8y8J_s-8cvq1GoTON6w__original/img/8wVEVzdf5BoPvSiscnGvCAPNe2Q=/0x0/filters:format(jpeg)/pic7416519.jpg', true, 7, 3, '7 Wonders (Second Edition)', 316377, 30, '7 Wonders is a highly acclaimed board game that has received over 30 international awards and sold over a million copies worldwide. It is a game of strategic decision-making, where players simultaneously play cards to develop their cities over three rounds. At the end of all three rounds, victory points are tallied based on the player''s city, wonder, military strength, and treasury, and the player with the highest score wins. The game features beautiful artwork and a simple and elegant drafting mechanic that allows up to 7 players to play with no dead time.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (273, 12, 'https://cf.geekdo-images.com/9YEjZIVdWVcUVWC7o8aKsQ__original/img/FmxZfRUytUU35lwbNkTetKJT6YM=/0x0/filters:format(jpeg)/pic1542342.jpg', true, 4, 2, 'Bora Bora', 127060, 120, 'Stake your fortunes in the mysterious island world of Bora Bora. Journey across islands, building huts, discovering fishing grounds, and sending priests to temples to gather offerings. Players use dice to strategically place actions, aiming to outwit opponents and make the most effective choices to win the game.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (274, 8, 'https://cf.geekdo-images.com/3h9W8BfB_rltQ48EBmHliw__original/img/DDk3fDr0FkDpkYAcny5LeekB2qg=/0x0/filters:format(png)/pic3132685.png', true, 4, 2, 'Kingdomino', 204583, 25, 'In Kingdomino, players take on the role of lords competing to expand their kingdom by exploring different terrains. Using domino-like tiles, players strategically connect their tiles to create a 5x5 grid. The game ends when each player completes their grid or is unable to do so, and points are awarded based on the number of connected tiles and valuable crown symbols.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (300, 10, 'https://cf.geekdo-images.com/GJKuOCy5HGAYgVH_Rei5mA__original/img/oRsIxp72iv58128KFdqvjG6Gz64=/0x0/filters:format(jpeg)/pic7070194.jpg', true, 2, 2, 'Onitama', 160477, 20, 'Onitama is a 2-player abstract game where players start with 5 pawns on a 5x5 board. Each player has 2 cards that show possible moves for their pieces. Players take turns choosing a card, moving a piece, and then exchanging that card with a 5th card. The objective is to capture the opponent''s pawns or their main pawn.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (275, 13, 'https://cf.geekdo-images.com/8qyLCqvdZkSpT2lEbwVpeg__original/img/8oGnjG0FUHy9Mo6VkbZVXZmDnwU=/0x0/filters:format(jpeg)/pic2305263.jpg', true, 4, 2, 'Arcadia Quest', 155068, 60, 'Arcadia Quest is a campaign-based game where players lead guilds of heroes to overthrow a vampire lord and take control of Arcadia. With the option to battle against each other or against monsters, players must complete quests to progress through the campaign. Each scenario offers different paths and the ability to unlock new weapons and abilities for the heroes.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (276, 12, 'https://cf.geekdo-images.com/jhORjBZcaM22fWiLU946aQ__original/img/fKCKLR_MRvqI-JqJ69K1qFFO8aI=/0x0/filters:format(jpeg)/pic6975876.jpg', true, 2, 2, 'Battle Line', 760, 30, 'Battle Line is a strategic card game where two opponents face off to win the battle by obtaining 5 out of 9 flags or 3 adjacent flags. Players place cards into poker-type hands on either side of the flag, and the side with the highest card formation wins the flag. It is a rethemed version of Schotten Totten with different graphics, wooden flag bits, and minor rule variations.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (277, 12, 'https://cf.geekdo-images.com/Go8mEe3K1VjYFJxLXEUPqQ__original/img/RcJ7xX5K_y0G87nK5ttdcWeb10U=/0x0/filters:format(jpeg)/pic1795272.jpg', true, 5, 2, 'Rococo', 144344, 120, 'Welcome to the Rococo era where you''ll manage the most prestigious ball of the era. In this Eurostyle board game, you''ll play employee cards to hire, buy resources, manufacture clothing, and invest in decorations. Choose and lead your employees wisely as their unique bonuses generate prestige points, and the player with the most prestige points at the end wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (278, 14, 'https://cf.geekdo-images.com/gLnzfyJio2MYbG9J-uQSiQ__original/img/WIqdfw845duHlpr9d5QvzPuHpGI=/0x0/filters:format(jpeg)/pic5253984.jpg', true, 7, 3, 'Forgotten Waters', 302723, 240, 'Forgotten Waters is a fantastical pirate adventure game where players sail together on a ship, pursuing personal stories and a common goal. With silly and magical elements, players explore and interact with the world, making choices that have lasting impacts on the story. The game offers five scenarios and a large location book for players to discover and make choices from.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (279, 15, 'https://cf.geekdo-images.com/HINb2nkFn5IiZxAlzQIs4g__original/img/e7izEwSmnBPiErsIF6hlWbgybBE=/0x0/filters:format(jpeg)/pic7009391.jpg', true, 20, 5, 'Blood on the Clocktower', 240980, 120, 'In Ravenswood Bluff, a quiet village, a demon is terrorizing the townsfolk. Players assume the roles of villagers with unique abilities, trying to deduce and eliminate the demon and its minions. The game consists of day and night phases, where players socialize and trade information during the day, and gather information or carry out actions at night. The game is played without a table or board, with a Storyteller guiding the game using playing pieces. Players can join late as powerful Traveler characters.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (280, 8, 'https://cf.geekdo-images.com/7b75ZZJQ4EEbDsKLi6kYSw__original/img/Oep0CGYNB5HiLBBOMmEo2qKgXaM=/0x0/filters:format(jpeg)/pic918568.jpg', true, 12, 3, 'Dixit: Odyssey', 92828, 30, 'Dixit Odyssey is a standalone game and expansion for Dixit. Each turn, one player is the storyteller and chooses a card to describe with a word or sentence. Other players choose cards that match the description, and everyone votes on which card belongs to the storyteller. The game includes new cards, a game board, scoring tokens, and can be played with up to twelve players.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (281, 14, 'https://cf.geekdo-images.com/awmZ9rYy-hTgea6Vdp_OoQ__original/img/bQ_yFa_JI_B8y3N4nfXcXYU_tE0=/0x0/filters:format(jpeg)/pic1299390.jpg', true, 4, 2, 'Seasons', 108745, 60, 'The competition to become the new archmage of the kingdom of Xidit is taking place in the Argos forest. In Seasons, players draft cards to build their decks and strategize for the main part of the game. They must then manage their resources, gain crystals, and adapt to changing seasons in order to earn victory points and become the ultimate sorcerer.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (282, 12, 'https://cf.geekdo-images.com/B21RQfIBnt2A_CYq8iZvrg__original/img/UXIVL5GrcjSmLRQwjCa-8fviulU=/0x0/filters:format(jpeg)/pic6220821.jpg', true, 4, 2, 'Anno 1800', 311193, 120, 'In Anno 1800, players build their own industry and trade with ship fleets to develop their home island and expand to new ones. The goal is to fulfill the wishes of the population by strategically planning production chains and balancing different types of workers. Compete against others to create the most prosperous island while staying vigilant against challenges from competitors.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (283, 14, 'https://cf.geekdo-images.com/UIKUXkcnH3Bd4fjcPGINcA__original/img/IrqDqjjm1LY66Zkw7l4RwppaBBI=/0x0/filters:format(png)/pic3304124.png', true, 6, 2, 'Ethnos', 206718, 60, 'In Ethnos, players recruit fantasy tribes such as giants, merfolk, and minotaurs to gain control of the land and collect glory tokens. Each player creates a deck with their chosen tribes and strategically plays cards to earn tokens and activate their tribe members'' powers. After three ages, the player with the most glory wins, with additional scoring occurring at the end of each age.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (284, 13, 'https://cf.geekdo-images.com/-aaspZ2Oq1VuAADvbUoeww__original/img/fr4sH1ViKPtRys-gjCY4dY8UuQE=/0x0/filters:format(jpeg)/pic3048617.jpg', true, 5, 1, 'The Manhattan Project: Energy Empire', 176734, 120, 'In The Manhattan Project: Energy Empire, players control nations vying for power in the 20th century. Through acquiring resources, building structures, and harnessing energy sources, players must strategically develop their nation''s industry, commerce, and government. Unlike conventional war-focused games, Energy Empire emphasizes the global impacts of industrialization and pollution, requiring players to carefully balance their actions to mitigate harm and avert crises.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (285, 13, 'https://cf.geekdo-images.com/khAHJpItPCZTbLKY9vUUCg__original/img/3q0qApiQvFJXJ7wboLSpWCjDbYc=/0x0/filters:format(jpeg)/pic840712.jpg', true, 6, 2, 'Imperial', 24181, 180, 'In Imperial, players take on the role of international investors in Europe during the age of imperialism. They strategically control the politics and economies of six powerful nations, erecting factories, building fleets, and deploying armies. The goal is to increase their capital and influence in order to win the imperial competition. The game is designed for two to six players, ages twelve and up, and typically lasts two to three hours.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (286, 10, 'https://cf.geekdo-images.com/qrPLpAnhFgc470ZRuXlvbg__original/img/QenUCVaFRry4-wlimHmeLn226Bo=/0x0/filters:format(jpeg)/pic4037705.jpg', true, 5, 3, 'Cryptid', 246784, 50, 'In Cryptid, players are cryptozoologists trying to uncover the truth behind an elusive creature. Through deduction and misdirection, players gather clues from opponents while keeping their own information hidden. The game features a modular board, clue books, and set-up cards with various difficulty levels, and also offers an optional digital companion for faster set-up and a wide range of puzzles.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (287, 9, 'https://cf.geekdo-images.com/fQe85tsBZoH6ibPnm1k1UA__original/img/eqUVrCJKcMBGBhw4Gfc9_9JabYE=/0x0/filters:format(jpeg)/pic791151.jpg', true, 2, 2, 'Hive', 2655, 20, 'Hive is a two-player strategic game that can be played on any flat surface. Each player has eleven black or white pieces representing different creatures with unique movements. The goal of the game is to surround your opponent''s queen while blocking them from surrounding yours.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (288, 12, 'https://cf.geekdo-images.com/qPNWng17gy-ghltZfNcBwA__original/img/mPJnr0qY9uAap4HKmwBoURzzaZ0=/0x0/filters:format(png)/pic6893215.png', true, 4, 1, 'Great Western Trail: Argentina', 364011, 150, 'In Great Western Trail: Argentina, players own a large estancia in Argentina during the 19th century and must navigate the plains of the Pampas to deliver cattle to the train station in Buenos Aires. This expansion features familiar gameplay elements from Great Western Trail, such as deck management and upgrading player boards, along with new features and twists. Players will encounter new paths, farmers as a new type of worker, and the opportunity to use cows to help farmers and earn grain as a new resource. Additionally, shortcuts can be unlocked for faster delivery, but players must carefully balance their actions and manage new cards to succeed.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (289, 14, 'https://cf.geekdo-images.com/dMKwaRT7SYvukMuz6A8cxQ__original/img/ujxqaJuxawHp28WMvjv4JyUp0ac=/0x0/filters:format(jpeg)/pic93485.jpg', true, 5, 2, 'Indonesia', 19777, 240, 'Indonesia is a strategic board game for 2-5 players where they compete to build up their economy and accumulate the most money. Players acquire production and shipping companies to deliver goods to growing cities, earning money for each delivery. They can research advantages, merge companies, and buy out other players to gain control of lucrative plantations or shipping routes. The player with the most money at the end of the game wins.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (290, 12, 'https://cf.geekdo-images.com/pTnv3lUWs4Knl3fqer0KnA__original/img/Sna_LGQ8KH3Zz6VjEAn-YQ-8yJw=/0x0/filters:format(jpeg)/pic2056160.jpg', true, 6, 2, 'Power Grid Deluxe: Europe/North America', 155873, 120, 'Power Grid Deluxe: Europe/North America is a standalone game in the Power Grid universe, commemorating the 10th anniversary of the original game. It features a double-sided game board showcasing Europe and North America, new wooden components, and a deck of new power plants. Players compete in auctions and resource management to expand their city networks and supply power, while balancing efficiency and wealth. Please note that the deluxe edition includes the Power Grid: The New Power Plants - Set 2 expansion.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (291, 13, 'https://cf.geekdo-images.com/7JjAm9RM8E2pbu5KCCbsTQ__original/img/67Yryf6M4Tkv6gDAUIsbqPyhJWA=/0x0/filters:format(jpeg)/pic1103979.jpg', true, 4, 2, 'Dungeon Petz', 97207, 90, 'In Dungeon Petz, you will take on the role of an imp family starting a new business of breeding and selling petz to Dungeon Lords. These petz are magical, playful, and sometimes angry monsters that require constant attention. Players must use unique worker placement mechanisms to prepare themselves for raising and selling these creatures, while also participating in contests to showcase their pets and earn additional points.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (292, 12, 'https://cf.geekdo-images.com/rdZb15WqByPZzdTKEvNz0Q__original/img/Bfuphp_LyNR24k8Y4wLXjeUDdNs=/0x0/filters:format(jpeg)/pic3246725.jpg', true, 5, 2, 'Navegador', 66589, 90, 'This game is set in the Portuguese Age of Discoveries and involves players contracting men, acquiring ships and buildings, establishing colonies, and trading goods to expand their wealth. Players compete to combine their privileges with achievements such as colonies, factories, discoveries, shipyards, and churches in order to win.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (293, 14, 'https://cf.geekdo-images.com/AUd4nmguSljr9qvkUCF2XQ__original/img/1wLZ2_huuT7TXul4uDxq7SsM5Ns=/0x0/filters:format(jpeg)/pic3527761.jpg', true, 4, 2, 'Cthulhu Wars', 139976, 120, 'Cthulhu Wars is a strategy boardgame based on the Cthulhu mythos. Players take on the roles of alien races and gods, competing to accumulate points on the Doom Track by completing tasks and unlocking spell books. With unique factions, monsters, and abilities, the game supports 2-4 players and can be expanded to support up to 8 players with additional factions and maps.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (294, 14, 'https://cf.geekdo-images.com/-19XPXmTn9QyyvqwpcFkBw__original/img/zgZcNusSm7mFMP3N6MJvJRe_gKY=/0x0/filters:format(jpeg)/pic5231006.jpg', true, 4, 1, 'Marvel United', 298047, 40, 'In Marvel United, iconic Marvel Heroes work together to stop a powerful Villain''s master plan. Each Villain has a unique plan with cards triggering different effects and challenges across locations. Heroes strategically choose cards from their decks to not only use their actions and superpowers, but also combine with other Heroes to achieve the impossible and save the day.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (295, 12, 'https://cf.geekdo-images.com/-CogwvQ7V19RQH0tE6msFw__original/img/mRZ48c_emFwHzpeFIW9PBi8huQc=/0x0/filters:format(png)/pic4171533.png', true, 4, 1, 'Star Realms: Frontiers', 230253, 45, 'Star Realms: Frontiers is a standalone Star Realms game that can be played with up to four players. It includes an 80-card trade deck, new scorecards, and can be combined with other Star Realms sets. The game features fast-paced space battles, deckbuilding mechanics, and the ability to play cooperatively or competitively.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (296, 17, 'https://cf.geekdo-images.com/4ipWVnxUSwnSVywPTitgvQ__original/img/8HVhyVZH9-j3g2vge43Xvji-vX0=/0x0/filters:format(png)/pic6346032.png', true, 16, 4, 'Monikers', 156546, 60, 'Monikers is a party game where players try to get their teammates to guess names by describing or imitating famous people. The game consists of three rounds: in the first round, players can give any clues except for the name; in the second round, players can use only one word; and in the final round, players can only use gestures and charades.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (297, 8, 'https://cf.geekdo-images.com/lJkCMk1jwCl-KHSPZJCPZQ__original/img/sWgmSr0KiOvb3iGINSg-2huDnSk=/0x0/filters:format(jpeg)/pic4441140.jpg', true, 6, 2, 'Dice Throne: Season Two – Battle Chest', 244271, 40, 'The Battle Chest contains all 4 sets from Season Two. Dice Throne is a fast-paced combat game for 2-6 players. Choose from a variety of heroes with unique abilities and dice sets, accumulate combat points to activate powerful cards and manipulate dice to defeat opponents in this exciting game from Season Two.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (298, 10, 'https://cf.geekdo-images.com/t_4cyWZs2IC5s6V8t-R_Uw__original/img/oeo2_Rhha99XiJXG2K28W6J2qjM=/0x0/filters:format(jpeg)/pic990261.jpg', true, 5, 2, 'Age of Empires III: The Age of Discovery', 22545, 120, 'Designed by Glenn Drover, this boardgame takes you back to the age of exploration and colonization. Play as a colonial power and compete to gain fame, fortune, and control over the New World. With three ages to progress through, you can embark on expeditions, establish colonies, grow your economy, and engage in warfare if necessary. The game can be played by 6 players with the addition of an expansion, which includes new components and a different colored set of figures. Don''t confuse it with Age of Discovery.');
INSERT INTO games (id, age, image, is_accepted, max_players, min_players, name, original_id, playing_time, short_description) VALUES (299, 12, 'https://cf.geekdo-images.com/oc3u6OQpJkf9TvDw4iI9xA__original/img/Hh2zbSnLEARMlNg0OzImf2-5FIw=/0x0/filters:format(jpeg)/pic4991936.jpg', true, 8, 4, 'Telestrations', 46213, 30, 'In this creative drawing game, players take turns drawing and guessing sketches based on a given word. The sketches are passed along to other players, who must then try to guess and draw the word they see. Telestrations includes sketchbooks, markers, a die, a sand-timer, and a wide selection of words to choose from.');
